// API基础URL配置
const API_BASE_URL =
  process.env.REACT_APP_API_URL ||
  (process.env.NODE_ENV === "production"
    ? "https://your-backend-url.render.com" // 修改为您的Render后端URL
    : "http://localhost:3001");

export default API_BASE_URL;
