import { useState, useEffect } from "react";
import { useNavigate, useLocation, Link } from "react-router-dom";
import ForceGraph2D from "react-force-graph-2d";
import { useAuth } from "../context/AuthContext";
import logo from "../images/logo.svg";

// API 基础 URL
const API_BASE_URL = "http://localhost:3001/api";

export default function ResultPage() {
  const navigate = useNavigate();
  const location = useLocation();
  const { user } = useAuth();
  const [isProfileMenuOpen, setIsProfileMenuOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [sortBy, setSortBy] = useState("match");
  const [results, setResults] = useState([]);
  const [graphData, setGraphData] = useState({ nodes: [], links: [] });
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState("");
  const [searchParams, setSearchParams] = useState(
    location.state?.searchParams || {}
  );

  // 添加点击外部关闭菜单的处理函数
  useEffect(() => {
    const closeMenu = (e) => {
      if (isProfileMenuOpen && !e.target.closest(".profile-menu-container")) {
        setIsProfileMenuOpen(false);
      }
    };

    document.addEventListener("click", closeMenu);
    return () => document.removeEventListener("click", closeMenu);
  }, [isProfileMenuOpen]);

  // 页面加载时获取数据
  useEffect(() => {
    if (location.state?.searchParams) {
      fetchMatchingResults();
    }
  }, [location.state]);

  const fetchMatchingResults = async () => {
    try {
      setIsLoading(true);
      setError("");

      const token = localStorage.getItem("token");
      if (!token) {
        throw new Error("请先登录");
      }

      const response = await fetch(`${API_BASE_URL}/partner-search/find-partners`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(searchParams),
      });

      if (!response.ok) {
        throw new Error("获取匹配结果失败");
      }

      const data = await response.json();
      if (data.code === 0 && data.data) {
        setResults(data.data);
        generateGraphData(data.data);
      } else {
        setError(data.message || "未找到匹配结果");
      }
    } catch (error) {
      setError(error.message);
    } finally {
      setIsLoading(false);
    }
  };

  const generateGraphData = (matchResults) => {
    if (!matchResults || matchResults.length === 0) {
      setGraphData({ nodes: [], links: [] });
      return;
    }

    const nodes = matchResults.map((result) => ({
      id: result._id,
      name: result.name || result.orgName,
      matchCategory: result.matchCategory,
      score: result.matchScore,
      color: getColorByCategory(result.matchCategory),
    }));

    nodes.push({
      id: "current-user",
      name: user?.orgName || "Your Organization",
      color: "#4CAF50",
    });

    const links = nodes
      .filter((node) => node.id !== "current-user")
      .map((node) => ({
        source: "current-user",
        target: node.id,
        value: (node.score || 50) / 100,
      }));

    setGraphData({ nodes, links });
  };

  const getColorByCategory = (category) => {
    const colorMap = {
      "🌟 极佳匹配": "#4CAF50",
      "✅ 良好匹配": "#2196F3",
      "🟡 一般匹配": "#FFC107",
      "🔴 低匹配": "#FF5722",
      "⚫️ 不匹配": "#9E9E9E",
    };
    return colorMap[category] || "#9E9E9E";
  };

  const handleSearch = (e) => {
    e.preventDefault();
    const filteredResults = results.filter(
      (result) =>
        (result.name?.toLowerCase() || "").includes(searchQuery.toLowerCase()) ||
        (result.location?.toLowerCase() || "").includes(searchQuery.toLowerCase())
    );
    setResults(filteredResults);
  };

  const handleReset = () => {
    setSearchQuery("");
    fetchMatchingResults();
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* 顶部导航栏 */}
      <div
        className="App-header"
        style={{ height: "auto", minHeight: "auto", padding: "0" }}
      >
        <nav>
          <div className="logo">
            <img src={logo} alt="logo" />
            <span>Cause-connect</span>
          </div>
          <div className="nav-links">
            <a href="#">Explore</a>
            <Link to="/partnership-search">Partnership</Link>
            <a href="#">Solutions</a>
            <div className="profile-menu-container relative">
              <div
                className="flex items-center gap-1 ml-4 cursor-pointer"
                onClick={() => setIsProfileMenuOpen(!isProfileMenuOpen)}
              >
                <div className="w-8 h-8 rounded-full bg-gray-100 flex items-center justify-center">
                  <svg
                    className="w-5 h-5 text-gray-600"
                    fill="none"
                    stroke="currentColor"
                    viewBox="0 0 24 24"
                  >
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      strokeWidth="2"
                      d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z"
                    />
                  </svg>
                </div>
                <span className="text-sm text-gray-700 font-medium">
                  {user?.orgName}
                </span>
                <svg
                  className={`w-4 h-4 text-gray-500 transition-transform duration-200 ${
                    isProfileMenuOpen ? "transform rotate-180" : ""
                  }`}
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth="2"
                    d="M19 9l-7 7-7-7"
                  />
                </svg>
              </div>

              {/* 下拉菜单 */}
              {isProfileMenuOpen && (
                <div className="absolute right-0 top-full mt-1 w-48 rounded-md shadow-lg bg-white ring-1 ring-black ring-opacity-5 z-50">
                  <div className="py-1">
                    <a
                      href="#"
                      className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
                    >
                      Profile Settings
                    </a>
                    <button
                      onClick={() => {
                        localStorage.removeItem("token");
                        window.location.href = "/";
                      }}
                      className="block w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
                    >
                      Sign Out
                    </button>
                  </div>
                </div>
              )}
            </div>
          </div>
        </nav>
      </div>

      {/* 主要内容区域 */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="mb-6">
          <h1 className="text-2xl font-semibold text-gray-900">
            为您找到的合作伙伴
          </h1>
          {searchParams && Object.keys(searchParams).length > 0 && (
            <div className="mt-2 text-sm text-gray-500">
              <span>搜索条件: </span>
              {searchParams.location && (
                <span className="mr-2">地点: {searchParams.location}</span>
              )}
              {searchParams.organizationType && (
                <span className="mr-2">
                  组织类型: {searchParams.organizationType}
                </span>
              )}
            </div>
          )}
        </div>

        <div className="flex flex-col md:flex-row gap-8">
          {/* 左侧搜索和结果列表 */}
          <div className="w-full md:w-1/2">
            {/* 搜索和过滤器 */}
            {results.length > 0 && (
              <div className="space-y-4 mb-6">
                <form onSubmit={handleSearch} className="flex gap-4">
                  <input
                    type="text"
                    placeholder="搜索关键词"
                    className="flex-1 px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-teal-500 focus:border-transparent"
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                  />
                  <button
                    type="submit"
                    className="px-4 py-2 bg-teal-500 text-white rounded-lg hover:bg-teal-600"
                  >
                    搜索
                  </button>
                  <button
                    type="button"
                    onClick={handleReset}
                    className="px-4 py-2 bg-gray-500 text-white rounded-lg hover:bg-gray-600"
                  >
                    重置
                  </button>
                </form>
                <div className="flex gap-4">
                  <select
                    className="px-4 py-2 bg-gray-100 text-gray-700 rounded-lg hover:bg-gray-200"
                    value={sortBy}
                    onChange={(e) => {
                      setSortBy(e.target.value);
                      const sortedResults = [...results].sort((a, b) => {
                        if (e.target.value === "match") {
                          return (b.matchScore || 0) - (a.matchScore || 0);
                        } else if (e.target.value === "name") {
                          return (a.name || "").localeCompare(b.name || "");
                        } else if (e.target.value === "location") {
                          return (a.location || "").localeCompare(b.location || "");
                        }
                        return 0;
                      });
                      setResults(sortedResults);
                    }}
                  >
                    <option value="match">按匹配度排序</option>
                    <option value="name">按名称排序</option>
                    <option value="location">按地点排序</option>
                  </select>
                </div>
              </div>
            )}

            {/* 错误提示 */}
            {error && (
              <div className="mb-4 p-3 bg-red-100 text-red-700 rounded-lg">
                {error}
              </div>
            )}

            {/* 加载状态 */}
            {isLoading ? (
              <div className="flex justify-center items-center h-64">
                <svg
                  className="animate-spin h-10 w-10 text-teal-500"
                  xmlns="http://www.w3.org/2000/svg"
                  fill="none"
                  viewBox="0 0 24 24"
                >
                  <circle
                    className="opacity-25"
                    cx="12"
                    cy="12"
                    r="10"
                    stroke="currentColor"
                    strokeWidth="4"
                  ></circle>
                  <path
                    className="opacity-75"
                    fill="currentColor"
                    d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
                  ></path>
                </svg>
              </div>
            ) : (
              <>
                {/* 结果列表 */}
                {results.length === 0 ? (
                  <div className="text-center py-10">
                    <p className="text-gray-500">
                      未找到匹配的合作伙伴。请尝试调整搜索条件。
                    </p>
                  </div>
                ) : (
                  <div className="space-y-4">
                    {results.map((result) => (
                      <div
                        key={result._id}
                        className="bg-white p-6 rounded-xl shadow-sm hover:shadow-md transition-shadow"
                      >
                        <div className="flex justify-between items-start mb-4">
                          <div className="flex-grow">
                            <h3 className="text-xl font-semibold text-gray-900">
                              {result.Name || result.name || "未知组织"}
                            </h3>
                            <div className="flex gap-2 mt-2">
                              {result.matchCategory && (
                                <span
                                  className="px-2 py-1 text-sm rounded"
                                  style={{
                                    backgroundColor: `${getColorByCategory(
                                      result.matchCategory
                                    )}20`,
                                    color: getColorByCategory(
                                      result.matchCategory
                                    ),
                                  }}
                                >
                                  {result.matchCategory}
                                </span>
                              )}
                              <span className="text-2xl font-bold text-teal-500">
                                {result.matchScore ? `${result.matchScore}%` : ""}
                              </span>
                            </div>
                          </div>
                        </div>

                        <div className="space-y-4">
                          {/* 位置信息 */}
                          {(result.City || result.State) && (
                            <div className="flex items-start gap-2">
                              <svg className="w-5 h-5 text-gray-400 mt-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z"/>
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M15 11a3 3 0 11-6 0 3 3 0 016 0z"/>
                              </svg>
                              <div>
                                <p className="text-gray-600">
                                  {[result.City, result.State].filter(Boolean).join(", ")}
                                </p>
                              </div>
                            </div>
                          )}

                          {/* 组织描述 */}
                          {(result.Description || result.linkedin_description) && (
                            <div className="flex items-start gap-2">
                              <svg className="w-5 h-5 text-gray-400 mt-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z"/>
                              </svg>
                              <div>
                                <p className="text-gray-600">
                                  {result.Description || result.linkedin_description}
                                </p>
                              </div>
                            </div>
                          )}

                          {/* 行业和专长 */}
                          {(result.linkedin_industries || result.linkedin_specialities) && (
                            <div className="flex items-start gap-2">
                              <svg className="w-5 h-5 text-gray-400 mt-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M21 13.255A23.931 23.931 0 0112 15c-3.183 0-6.22-.62-9-1.745M16 6V4a2 2 0 00-2-2h-4a2 2 0 00-2 2v2m4 6h.01M5 20h14a2 2 0 002-2V8a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z"/>
                              </svg>
                              <div>
                                {result.linkedin_industries && (
                                  <p className="text-gray-600">
                                    <span className="font-medium">行业：</span>
                                    {result.linkedin_industries}
                                  </p>
                                )}
                                {result.linkedin_specialities && (
                                  <p className="text-gray-600">
                                    <span className="font-medium">专长：</span>
                                    {result.linkedin_specialities}
                                  </p>
                                )}
                              </div>
                            </div>
                          )}

                          {/* 标签 */}
                          {result.matchTags && Array.isArray(result.matchTags) && result.matchTags.length > 0 && (
                            <div className="flex flex-wrap gap-2 mt-3">
                              {result.matchTags.map((tag, index) => (
                                <span
                                  key={index}
                                  className="px-2 py-1 bg-gray-100 text-gray-600 text-sm rounded-full"
                                >
                                  {tag}
                                </span>
                              ))}
                            </div>
                          )}

                          {/* 链接 */}
                          <div className="flex gap-4 mt-4">
                            {result.URL && (
                              <a
                                href={result.URL}
                                target="_blank"
                                rel="noopener noreferrer"
                                className="flex items-center text-teal-500 hover:text-teal-600"
                              >
                                <svg className="w-4 h-4 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 6H6a2 2 0 00-2 2v10a2 2 0 002 2h10a2 2 0 002-2v-4M14 4h6m0 0v6m0-6L10 14"/>
                                </svg>
                                访问网站
                              </a>
                            )}
                            {result.linkedin_url && (
                              <a
                                href={result.linkedin_url}
                                target="_blank"
                                rel="noopener noreferrer"
                                className="flex items-center text-blue-600 hover:text-blue-700"
                              >
                                <svg className="w-4 h-4 mr-1" fill="currentColor" viewBox="0 0 24 24">
                                  <path d="M19 0h-14c-2.761 0-5 2.239-5 5v14c0 2.761 2.239 5 5 5h14c2.762 0 5-2.239 5-5v-14c0-2.761-2.238-5-5-5zm-11 19h-3v-11h3v11zm-1.5-12.268c-.966 0-1.75-.79-1.75-1.764s.784-1.764 1.75-1.764 1.75.79 1.75 1.764-.783 1.764-1.75 1.764zm13.5 12.268h-3v-5.604c0-3.368-4-3.113-4 0v5.604h-3v-11h3v1.765c1.396-2.586 7-2.777 7 2.476v6.759z"/>
                                </svg>
                                LinkedIn
                              </a>
                            )}
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </>
            )}
          </div>

          {/* 右侧网络图 */}
          <div className="w-full md:w-1/2 bg-white rounded-xl shadow-sm p-4 mt-6 md:mt-0">
            <h2 className="text-lg font-semibold mb-4">网络关系图</h2>
            <div className="h-[600px]">
              {graphData.nodes.length > 0 ? (
                <ForceGraph2D
                  graphData={graphData}
                  nodeColor={(node) => node.color}
                  nodeLabel={(node) =>
                    `${node.name}${
                      node.matchCategory ? ` (${node.matchCategory})` : ""
                    }`
                  }
                  linkWidth={(link) => link.value * 3}
                  linkColor={() => "#ddd"}
                  nodeRelSize={6}
                  nodeSize={(node) => node.id === "current-user" ? 10 : 6}
                  backgroundColor="#ffffff"
                />
              ) : (
                <div className="flex justify-center items-center h-full">
                  <p className="text-gray-500">
                    暂无数据可供可视化展示
                  </p>
                </div>
              )}
            </div>
            {results.length > 0 && (
              <div className="mt-4 flex flex-wrap justify-end gap-4">
                <div className="flex items-center gap-2">
                  <span className="w-3 h-3 rounded-full bg-[#4CAF50]"></span>
                  <span className="text-sm text-gray-600">🌟 极佳匹配</span>
                </div>
                <div className="flex items-center gap-2">
                  <span className="w-3 h-3 rounded-full bg-[#2196F3]"></span>
                  <span className="text-sm text-gray-600">✅ 良好匹配</span>
                </div>
                <div className="flex items-center gap-2">
                  <span className="w-3 h-3 rounded-full bg-[#FFC107]"></span>
                  <span className="text-sm text-gray-600">🟡 一般匹配</span>
                </div>
                <div className="flex items-center gap-2">
                  <span className="w-3 h-3 rounded-full bg-[#FF5722]"></span>
                  <span className="text-sm text-gray-600">🔴 低匹配</span>
                </div>
                <div className="flex items-center gap-2">
                  <span className="w-3 h-3 rounded-full bg-[#9E9E9E]"></span>
                  <span className="text-sm text-gray-600">⚫️ 不匹配</span>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
