import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import heroImage from "../images/hero-bg.jpg";

export default function ProfileTypePage() {
  const navigate = useNavigate();
  const { user } = useAuth();
  const [selectedType, setSelectedType] = useState("");

  const handleTypeSelect = (type) => {
    localStorage.setItem("orgType", type);
    navigate("/profile-setup/details");
  };

  useEffect(() => {
    console.log("ProfileTypePage: 页面已加载");
    if (!user) {
      console.log("未登录用户，重定向到登录页面");
      navigate("/signin");
    }
  }, [user, navigate]);

  return (
    <div className="min-h-screen flex">
      <div className="flex-1 flex flex-col justify-center px-4 sm:px-6 lg:px-20 xl:px-24">
        <div className="mx-auto w-full max-w-sm">
          <h2 className="mt-6 text-3xl font-bold text-gray-900">
            Tell us about your organization
          </h2>
          <p className="mt-2 text-sm text-gray-600">
            Select your organization type to help us provide better partnership
            matches
          </p>

          <div className="mt-8 space-y-4">
            <button
              onClick={() => handleTypeSelect("nonprofit")}
              className="w-full flex items-center justify-center px-8 py-6 border-2 border-gray-300 rounded-xl hover:border-gray-400 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gray-500"
            >
              <div className="text-left">
                <span className="text-2xl mb-2">🌍</span>
                <h3 className="text-lg font-medium text-gray-900">
                  We're Nonprofits
                </h3>
                <p className="text-sm text-gray-500">
                  NGOs, charities, and social organizations
                </p>
              </div>
            </button>

            <button
              onClick={() => handleTypeSelect("for-profit")}
              className="w-full flex items-center justify-center px-8 py-6 border-2 border-gray-300 rounded-xl hover:border-gray-400 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gray-500"
            >
              <div className="text-left">
                <span className="text-2xl mb-2">💼</span>
                <h3 className="text-lg font-medium text-gray-900">
                  We're For-Profits
                </h3>
                <p className="text-sm text-gray-500">
                  Companies and businesses looking to make an impact
                </p>
              </div>
            </button>
          </div>
        </div>
      </div>
      <div className="hidden lg:block relative flex-1">
        <img
          className="absolute inset-0 h-full w-full object-cover"
          src={heroImage}
          alt="Background"
        />
      </div>
    </div>
  );
}
