import { useState, useEffect } from "react";
import { useNavigate, useLocation, Link } from "react-router-dom";
import ForceGraph2D from "react-force-graph-2d";
import { useAuth } from "../context/AuthContext";
import logo from "../images/logo.svg";

// API 基础 URL
const API_BASE_URL = "http://localhost:3001/api";

export default function ResultPage() {
  const navigate = useNavigate();
  const location = useLocation();
  const { user } = useAuth();
  const [isProfileMenuOpen, setIsProfileMenuOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [sortBy, setSortBy] = useState("match");
  const [results, setResults] = useState([]);
  const [graphData, setGraphData] = useState({ nodes: [], links: [] });
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState("");
  const [searchParams, setSearchParams] = useState(
    location.state?.searchParams || {}
  );
  const [selectedFilters, setSelectedFilters] = useState([]);

  // 添加点击外部关闭菜单的处理函数
  useEffect(() => {
    const closeMenu = (e) => {
      if (isProfileMenuOpen && !e.target.closest(".profile-menu-container")) {
        setIsProfileMenuOpen(false);
      }
    };

    document.addEventListener("click", closeMenu);
    return () => document.removeEventListener("click", closeMenu);
  }, [isProfileMenuOpen]);

  // 页面加载时获取数据
  useEffect(() => {
    if (location.state?.searchParams) {
      // 初始化已选过滤条件
      const initialFilters = [];
      if (searchParams.location)
        initialFilters.push({ type: "location", value: searchParams.location });
      if (searchParams.organizationType)
        initialFilters.push({
          type: "orgType",
          value: searchParams.organizationType,
        });
      setSelectedFilters(initialFilters);

      fetchMatchingResults();
    }
  }, [location.state]);

  const fetchMatchingResults = async () => {
    try {
      setIsLoading(true);
      setError("");

      const token = localStorage.getItem("token");
      if (!token) {
        throw new Error("请先登录");
      }

      const response = await fetch(
        `${API_BASE_URL}/partner-search/find-partners`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify(searchParams),
        }
      );

      if (!response.ok) {
        throw new Error("获取匹配结果失败");
      }

      const data = await response.json();
      if (data.code === 0 && data.data) {
        setResults(data.data);
        generateGraphData(data.data);
      } else {
        setError(data.message || "未找到匹配结果");
      }
    } catch (error) {
      setError(error.message);
    } finally {
      setIsLoading(false);
    }
  };

  const generateGraphData = (matchResults) => {
    if (!matchResults || matchResults.length === 0) {
      setGraphData({ nodes: [], links: [] });
      return;
    }

    const nodes = matchResults.map((result) => ({
      id: result._id,
      name: result.Name || result.name || "未知组织",
      matchCategory: result.matchCategory,
      score: result.matchScore,
      color: getColorByCategory(result.matchCategory),
    }));

    nodes.push({
      id: "current-user",
      name: user?.orgName || "Your Organization",
      color: "#4CAF50",
    });

    const links = nodes
      .filter((node) => node.id !== "current-user")
      .map((node) => ({
        source: "current-user",
        target: node.id,
        value: (node.score || 50) / 100,
      }));

    setGraphData({ nodes, links });
  };

  const getColorByCategory = (category) => {
    const colorMap = {
      "Perfect Match": "#4CAF50",
      "Good Match": "#2196F3",
      "⭐ Excellent Match": "#4CAF50",
      "✅ Good Match": "#2196F3",
      "🟡 Average Match": "#FFC107",
      "🔴 Low Match": "#FF5722",
    };
    return colorMap[category] || "#9E9E9E";
  };

  const handleSearch = (e) => {
    e.preventDefault();
    const filteredResults = results.filter(
      (result) =>
        (result.name?.toLowerCase() || "").includes(
          searchQuery.toLowerCase()
        ) ||
        (result.location?.toLowerCase() || "").includes(
          searchQuery.toLowerCase()
        )
    );
    setResults(filteredResults);
  };

  const handleReset = () => {
    setSearchQuery("");
    fetchMatchingResults();
  };

  const removeFilter = (filterToRemove) => {
    const updatedFilters = selectedFilters.filter(
      (filter) =>
        !(
          filter.type === filterToRemove.type &&
          filter.value === filterToRemove.value
        )
    );
    setSelectedFilters(updatedFilters);

    // 更新搜索参数并重新获取结果
    const updatedParams = { ...searchParams };
    if (filterToRemove.type === "location") delete updatedParams.location;
    if (filterToRemove.type === "orgType")
      delete updatedParams.organizationType;

    setSearchParams(updatedParams);
    // fetchMatchingResults(); // 可以根据需要添加
  };

  const handleAISuggestions = () => {
    // 处理 AI 建议按钮点击
    console.log("AI Suggestions clicked");
    // 这里可以添加 AI 建议的逻辑
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* 顶部导航栏 */}
      <header className="bg-white shadow-sm">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                <Link to="/" className="flex items-center">
                  <span className="text-xl font-semibold text-green-600">
                    Cause
                  </span>
                  <span className="text-xl font-semibold text-gray-700">
                    Connect
                  </span>
                </Link>
              </div>
            </div>
            <nav className="flex space-x-8">
              <Link
                to="/explore"
                className="text-gray-500 hover:text-gray-700 px-3 py-2 text-sm font-medium"
              >
                Explore
              </Link>
              <Link
                to="/partnership-search"
                className="text-gray-900 border-b-2 border-green-500 px-3 py-2 text-sm font-medium"
              >
                Partnership
              </Link>
              <Link
                to="/solutions"
                className="text-gray-500 hover:text-gray-700 px-3 py-2 text-sm font-medium"
              >
                Solutions
              </Link>
            </nav>
            <div className="profile-menu-container relative">
              <div
                className="flex items-center gap-2 cursor-pointer"
                onClick={() => setIsProfileMenuOpen(!isProfileMenuOpen)}
              >
                <div className="w-8 h-8 rounded-full bg-gray-200 flex items-center justify-center overflow-hidden">
                  {user?.photoURL ? (
                    <img
                      src={user.photoURL}
                      alt="Profile"
                      className="w-full h-full object-cover"
                    />
                  ) : (
                    <svg
                      className="w-5 h-5 text-gray-600"
                      fill="none"
                      stroke="currentColor"
                      viewBox="0 0 24 24"
                    >
                      <path
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        strokeWidth="2"
                        d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z"
                      />
                    </svg>
                  )}
                </div>
              </div>

              {/* 下拉菜单 */}
              {isProfileMenuOpen && (
                <div className="absolute right-0 top-full mt-1 w-48 rounded-md shadow-lg bg-white ring-1 ring-black ring-opacity-5 z-50">
                  <div className="py-1">
                    <a
                      href="#"
                      className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
                    >
                      Profile Settings
                    </a>
                    <button
                      onClick={() => {
                        localStorage.removeItem("token");
                        window.location.href = "/";
                      }}
                      className="block w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
                    >
                      Sign Out
                    </button>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </header>

      {/* 主要内容区域 */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="mb-8">
          <h1 className="text-2xl font-medium text-gray-900">
            Partnerships for you
          </h1>
        </div>

        {/* 搜索和过滤器区域 */}
        <div className="bg-white rounded-lg shadow-sm p-6 mb-8">
          <div className="flex flex-col space-y-4">
            <div className="flex items-center space-x-4">
              {/* 搜索框 */}
              <div className="relative flex-1">
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <svg
                    className="h-5 w-5 text-gray-400"
                    xmlns="http://www.w3.org/2000/svg"
                    viewBox="0 0 20 20"
                    fill="currentColor"
                  >
                    <path
                      fillRule="evenodd"
                      d="M8 4a4 4 0 100 8 4 4 0 000-8zM2 8a6 6 0 1110.89 3.476l4.817 4.817a1 1 0 01-1.414 1.414l-4.816-4.816A6 6 0 012 8z"
                      clipRule="evenodd"
                    />
                  </svg>
                </div>
                <input
                  type="text"
                  placeholder="Clean energy"
                  className="w-full pl-10 pr-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-1 focus:ring-green-500 focus:border-green-500"
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                />
              </div>

              {/* 排序按钮 */}
              <div className="relative">
                <button className="flex items-center space-x-2 px-4 py-2 border border-gray-300 rounded-lg bg-white text-gray-700 hover:bg-gray-50">
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    className="h-5 w-5 text-gray-500"
                    viewBox="0 0 20 20"
                    fill="currentColor"
                  >
                    <path d="M5 4a1 1 0 00-2 0v7.268a2 2 0 000 3.464V16a1 1 0 102 0v-1.268a2 2 0 000-3.464V4zM11 4a1 1 0 10-2 0v1.268a2 2 0 000 3.464V16a1 1 0 102 0V8.732a2 2 0 000-3.464V4zM16 3a1 1 0 011 1v7.268a2 2 0 010 3.464V16a1 1 0 11-2 0v-1.268a2 2 0 010-3.464V4a1 1 0 011-1z" />
                  </svg>
                  <span>Sort by</span>
                </button>
              </div>

              {/* 过滤按钮 */}
              <div className="relative">
                <button className="flex items-center space-x-2 px-4 py-2 border border-gray-300 rounded-lg bg-white text-gray-700 hover:bg-gray-50">
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    className="h-5 w-5 text-gray-500"
                    viewBox="0 0 20 20"
                    fill="currentColor"
                  >
                    <path
                      fillRule="evenodd"
                      d="M3 3a1 1 0 011-1h12a1 1 0 011 1v3a1 1 0 01-.293.707L12 11.414V15a1 1 0 01-.293.707l-2 2A1 1 0 018 17v-5.586L3.293 6.707A1 1 0 013 6V3z"
                      clipRule="evenodd"
                    />
                  </svg>
                  <span>Filter</span>
                </button>
              </div>

              {/* AI 建议按钮 */}
              <button
                onClick={handleAISuggestions}
                className="px-4 py-2 bg-gray-800 text-white rounded-lg hover:bg-gray-700 transition-colors"
              >
                AI Suggestions
              </button>
            </div>

            {/* 已选过滤器标签 */}
            {selectedFilters.length > 0 && (
              <div className="flex flex-wrap gap-2 mt-3">
                {selectedFilters.map((filter, index) => (
                  <div
                    key={index}
                    className="flex items-center space-x-1 px-3 py-1 bg-green-100 text-green-800 rounded-full text-sm"
                  >
                    <span>
                      {filter.type === "location"
                        ? filter.value
                        : filter.type === "orgType"
                        ? filter.value
                        : filter.value}
                    </span>
                    <button
                      onClick={() => removeFilter(filter)}
                      className="ml-1 text-green-600 hover:text-green-800"
                    >
                      <svg
                        xmlns="http://www.w3.org/2000/svg"
                        className="h-4 w-4"
                        viewBox="0 0 20 20"
                        fill="currentColor"
                      >
                        <path
                          fillRule="evenodd"
                          d="M4.293 4.293a1 1 0 011.414 0L10 8.586l4.293-4.293a1 1 0 111.414 1.414L11.414 10l4.293 4.293a1 1 0 01-1.414 1.414L10 11.414l-4.293 4.293a1 1 0 01-1.414-1.414L8.586 10 4.293 5.707a1 1 0 010-1.414z"
                          clipRule="evenodd"
                        />
                      </svg>
                    </button>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>

        {/* 错误提示 */}
        {error && (
          <div className="mb-4 p-3 bg-red-100 text-red-700 rounded-lg">
            {error}
          </div>
        )}

        <div className="flex flex-col lg:flex-row gap-8">
          {/* 左侧结果列表 */}
          <div className="lg:w-1/2 space-y-4">
            {/* 加载状态 */}
            {isLoading ? (
              <div className="flex justify-center items-center h-64">
                <svg
                  className="animate-spin h-10 w-10 text-green-500"
                  xmlns="http://www.w3.org/2000/svg"
                  fill="none"
                  viewBox="0 0 24 24"
                >
                  <circle
                    className="opacity-25"
                    cx="12"
                    cy="12"
                    r="10"
                    stroke="currentColor"
                    strokeWidth="4"
                  ></circle>
                  <path
                    className="opacity-75"
                    fill="currentColor"
                    d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
                  ></path>
                </svg>
              </div>
            ) : (
              <>
                {/* 结果列表 */}
                {results.length === 0 ? (
                  <div className="bg-white rounded-lg shadow-sm p-6 text-center">
                    <p className="text-gray-500">
                      未找到匹配的合作伙伴。请尝试调整搜索条件。
                    </p>
                  </div>
                ) : (
                  <>
                    {results.map((result) => (
                      <div
                        key={result._id}
                        className="bg-white rounded-lg shadow-sm overflow-hidden"
                      >
                        <div className="p-6">
                          {/* 组织名称和匹配标签 */}
                          <div className="flex items-start justify-between mb-4">
                            <div>
                              <h3 className="text-xl font-medium text-gray-900">
                                {result.Name || result.name || "未知组织"}
                              </h3>
                              <p className="text-gray-600 mt-1">
                                {result.City
                                  ? `${result.City}${
                                      result.State ? `, ${result.State}` : ""
                                    }`
                                  : ""}
                              </p>
                            </div>
                            {result.matchCategory && (
                              <span
                                className={`px-3 py-1 rounded-md text-sm font-medium ${
                                  result.matchCategory.includes("Perfect") ||
                                  result.matchCategory.includes("Excellent")
                                    ? "bg-green-100 text-green-800"
                                    : "bg-blue-100 text-blue-800"
                                }`}
                              >
                                {result.matchCategory.includes("Perfect") ||
                                result.matchCategory.includes("Excellent")
                                  ? "Perfect Match"
                                  : "Good Match"}
                                <svg
                                  className="inline-block w-4 h-4 ml-1"
                                  viewBox="0 0 20 20"
                                  fill="currentColor"
                                >
                                  <path
                                    fillRule="evenodd"
                                    d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z"
                                    clipRule="evenodd"
                                  />
                                </svg>
                              </span>
                            )}
                          </div>

                          {/* 标签 */}
                          <div className="flex flex-wrap gap-2 mb-4">
                            <span className="px-2 py-1 bg-gray-100 text-gray-800 text-xs rounded-full">
                              Non-Profit
                            </span>
                            <span className="px-2 py-1 bg-gray-100 text-gray-800 text-xs rounded-full">
                              Energy
                            </span>
                            {result.linkedin_industries && (
                              <span className="px-2 py-1 bg-gray-100 text-gray-800 text-xs rounded-full">
                                {result.linkedin_industries.split(",")[0]}
                              </span>
                            )}
                          </div>

                          {/* 右箭头 */}
                          <div className="flex justify-end mt-2">
                            <button className="text-gray-400 hover:text-gray-600">
                              <svg
                                className="w-6 h-6"
                                fill="none"
                                stroke="currentColor"
                                viewBox="0 0 24 24"
                              >
                                <path
                                  strokeLinecap="round"
                                  strokeLinejoin="round"
                                  strokeWidth="2"
                                  d="M9 5l7 7-7 7"
                                />
                              </svg>
                            </button>
                          </div>
                        </div>
                      </div>
                    ))}
                  </>
                )}
              </>
            )}
          </div>

          {/* 右侧网络图 */}
          <div className="lg:w-1/2">
            <div className="bg-white rounded-lg shadow-sm p-6">
              <h2 className="text-lg font-medium text-gray-900 mb-4">
                Network Map
              </h2>
              <div className="h-[500px]">
                {graphData.nodes.length > 0 ? (
                  <ForceGraph2D
                    graphData={graphData}
                    nodeColor={(node) => node.color}
                    nodeLabel={(node) =>
                      `${node.name}${
                        node.matchCategory ? ` (${node.matchCategory})` : ""
                      }`
                    }
                    linkWidth={(link) => link.value * 3}
                    linkColor={() => "#ddd"}
                    nodeRelSize={6}
                    nodeCanvasObject={(node, ctx, globalScale) => {
                      const label = node.name;
                      const fontSize = 12 / globalScale;
                      ctx.font = `${fontSize}px Sans-Serif`;
                      const textWidth = ctx.measureText(label).width;
                      const bckgDimensions = [textWidth, fontSize].map(
                        (n) => n + fontSize * 0.2
                      );

                      ctx.fillStyle = node.color;
                      ctx.beginPath();
                      ctx.arc(node.x, node.y, 5, 0, 2 * Math.PI, false);
                      ctx.fill();
                    }}
                    nodeSize={(node) => (node.id === "current-user" ? 10 : 6)}
                    backgroundColor="#ffffff"
                  />
                ) : (
                  <div className="flex justify-center items-center h-full">
                    <p className="text-gray-500">暂无数据可供可视化展示</p>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
