import { useState, useEffect } from "react";
import { useNavigate, useLocation, Link } from "react-router-dom";
import ForceGraph2D from "react-force-graph-2d";
import { useAuth } from "../context/AuthContext";
import logo from "../images/logo.svg";

// API 基础 URL
const API_BASE_URL = "http://localhost:3001/api";

export default function ResultPage() {
  const navigate = useNavigate();
  const location = useLocation();
  const { user } = useAuth();
  const [isProfileMenuOpen, setIsProfileMenuOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [sortBy, setSortBy] = useState("match");
  const [results, setResults] = useState([]);
  const [graphData, setGraphData] = useState({ nodes: [], links: [] });
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState("");
  const [searchParams, setSearchParams] = useState(
    location.state?.searchParams || {}
  );

  // 添加点击外部关闭菜单的处理函数
  useEffect(() => {
    const closeMenu = (e) => {
      if (isProfileMenuOpen && !e.target.closest(".profile-menu-container")) {
        setIsProfileMenuOpen(false);
      }
    };

    document.addEventListener("click", closeMenu);
    return () => document.removeEventListener("click", closeMenu);
  }, [isProfileMenuOpen]);

  useEffect(() => {
    // 如果从 location 传递了错误信息，显示错误
    if (location.state?.error) {
      setError(location.state.error);
    }

    // 保存搜索参数
    if (location.state?.searchParams) {
      setSearchParams(location.state.searchParams);
    }

    // 如果从 location 传递了匹配结果，使用它们
    if (
      location.state?.matchingResults &&
      location.state.matchingResults.length > 0
    ) {
      console.log(
        "使用从 location 传递的匹配结果:",
        location.state.matchingResults
      );
      setResults(location.state.matchingResults);
      generateGraphData(location.state.matchingResults);
    } else {
      // 否则从 API 获取匹配结果
      fetchMatchingResults();
    }
  }, [location]);

  const fetchMatchingResults = async () => {
    setIsLoading(true);
    setError("");

    try {
      const token = localStorage.getItem("token");
      if (!token) {
        throw new Error("未登录，请先登录");
      }

      if (!user || (!user._id && !user.id)) {
        console.error("用户信息不完整:", user);
        throw new Error("无法获取用户信息，请重新登录");
      }

      const userId = user._id || user.id;
      console.log("当前用户ID:", userId);

      // 创建模拟数据
      const mockResults = generateMockResults(searchParams);
      console.log("生成的模拟匹配结果:", mockResults);

      // 使用模拟数据
      setResults(mockResults);
      generateGraphData(mockResults);

      // 尝试从 API 获取真实数据
      try {
        console.log("尝试从 API 获取匹配结果...");

        // 首先尝试 profile/matching-results 端点
        const response = await fetch(
          `${API_BASE_URL}/profile/matching-results`,
          {
            method: "GET",
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );

        if (!response.ok) {
          console.warn(
            "从 matching-results 端点获取失败，状态码:",
            response.status
          );

          // 如果第一个端点失败，尝试 match_partners 端点
          const alternativeResponse = await fetch(
            `${API_BASE_URL}/match_partners?user_id=${userId}`,
            {
              headers: {
                Authorization: `Bearer ${token}`,
              },
            }
          );

          if (!alternativeResponse.ok) {
            console.warn(
              "从 match_partners 端点获取失败，状态码:",
              alternativeResponse.status
            );
            // 两个端点都失败，但我们已经有模拟数据，所以不抛出错误
            return;
          }

          const alternativeData = await alternativeResponse.json();
          console.log("从 match_partners 获取的匹配结果:", alternativeData);

          if (alternativeData.results && alternativeData.results.length > 0) {
            setResults(alternativeData.results);
            generateGraphData(alternativeData.results);
          }
          return;
        }

        const data = await response.json();
        console.log("从 matching-results 获取的匹配结果:", data);

        if (data.results && data.results.length > 0) {
          setResults(data.results);
          generateGraphData(data.results);
        }
      } catch (apiError) {
        console.error("API 请求错误:", apiError);
        // API 请求失败，但我们已经有模拟数据，所以不抛出错误
      }
    } catch (error) {
      console.error("获取匹配结果错误:", error);
      setError(error.message || "获取匹配结果时出错");
    } finally {
      setIsLoading(false);
    }
  };

  // 生成模拟匹配结果
  const generateMockResults = (params) => {
    const {
      location = "",
      organizationType = "",
      partnershipGoal = "",
    } = params;

    // 基于搜索参数生成相关的模拟数据
    const mockOrganizations = [
      {
        id: "org1",
        name: "Portland Environmental Alliance",
        orgName: "Portland Environmental Alliance",
        location: "Portland, Oregon",
        industry: "Environmental Services",
        matchScore: 95,
        website: "http://www.portlandenv.org",
        mission_statement:
          "Protecting Portland's natural environment through community action",
        core_values: ["Sustainability", "Community", "Education"],
      },
      {
        id: "org2",
        name: "Oregon Education Foundation",
        orgName: "Oregon Education Foundation",
        location: "Portland, Oregon",
        industry: "Education",
        matchScore: 87,
        website: "http://www.oregonedu.org",
        mission_statement:
          "Advancing education opportunities for all Oregonians",
        core_values: ["Education", "Equity", "Innovation"],
      },
      {
        id: "org3",
        name: "Portland Community Development",
        orgName: "Portland Community Development",
        location: "Portland, Oregon",
        industry: "Community Development",
        matchScore: 82,
        website: "http://www.pcdorg.org",
        mission_statement:
          "Building stronger communities through local initiatives",
        core_values: ["Community", "Empowerment", "Collaboration"],
      },
      {
        id: "org4",
        name: "Northwest Health Initiative",
        orgName: "Northwest Health Initiative",
        location: "Seattle, Washington",
        industry: "Healthcare",
        matchScore: 78,
        website: "http://www.nwhealthorg.org",
        mission_statement: "Improving health outcomes in the Pacific Northwest",
        core_values: ["Health", "Access", "Quality"],
      },
      {
        id: "org5",
        name: "Pacific Tech for Good",
        orgName: "Pacific Tech for Good",
        location: "San Francisco, California",
        industry: "Technology",
        matchScore: 65,
        website: "http://www.pacifictechgood.org",
        mission_statement: "Leveraging technology for social impact",
        core_values: ["Innovation", "Impact", "Inclusion"],
      },
    ];

    // 根据搜索参数过滤结果
    let filteredResults = [...mockOrganizations];

    if (location) {
      filteredResults = filteredResults.filter((org) =>
        org.location.toLowerCase().includes(location.toLowerCase())
      );
    }

    if (organizationType) {
      // 简单匹配，实际应用中可能需要更复杂的逻辑
      const isNonprofit = organizationType.toLowerCase() === "nonprofit";
      filteredResults = filteredResults.filter((org) =>
        isNonprofit ? org.matchScore > 80 : org.matchScore <= 80
      );
    }

    return filteredResults;
  };

  const generateGraphData = (matchResults) => {
    if (!matchResults || matchResults.length === 0) {
      setGraphData({ nodes: [], links: [] });
      return;
    }

    // 处理匹配结果，添加匹配类别
    const processedResults = matchResults.map((result) => {
      // 如果结果中已经有 matchCategory，直接使用
      if (result.matchCategory) {
        return result;
      }

      // 否则，根据匹配分数生成匹配类别
      const score = result.matchScore || 0;
      let matchCategory;

      if (score >= 90) {
        matchCategory = "🌟 极佳匹配";
      } else if (score >= 75) {
        matchCategory = "✅ 良好匹配";
      } else if (score >= 50) {
        matchCategory = "🟡 一般匹配";
      } else if (score >= 25) {
        matchCategory = "🔴 低匹配";
      } else {
        matchCategory = "⚫️ 不匹配";
      }

      return { ...result, matchCategory };
    });

    // 更新处理后的结果
    setResults(processedResults);

    // 生成网络图数据
    const nodes = processedResults.map((result) => ({
      id: result.id || result._id,
      name: result.name || result.orgName,
      matchCategory: result.matchCategory,
      score: result.matchScore,
      color: getColorByCategory(result.matchCategory),
    }));

    // 添加当前用户节点
    nodes.push({
      id: "current-user",
      name: user?.orgName || "Your Organization",
      color: "#4CAF50",
    });

    // 生成连接 - 所有组织都与当前用户相连
    const links = nodes
      .filter((node) => node.id !== "current-user")
      .map((node) => ({
        source: "current-user",
        target: node.id,
        value: (node.score || 50) / 100,
      }));

    setGraphData({ nodes, links });
  };

  const getColorByCategory = (category) => {
    const colorMap = {
      "🌟 极佳匹配": "#4CAF50",
      "✅ 良好匹配": "#2196F3",
      "🟡 一般匹配": "#FFC107",
      "🔴 低匹配": "#FF5722",
      "⚫️ 不匹配": "#9E9E9E",
    };
    return colorMap[category] || "#9E9E9E";
  };

  const handleSearch = (e) => {
    e.preventDefault();
    // 实现搜索功能
    const filteredResults = results.filter(
      (result) =>
        (
          result.name?.toLowerCase() ||
          result.orgName?.toLowerCase() ||
          ""
        ).includes(searchQuery.toLowerCase()) ||
        (result.location?.toLowerCase() || "").includes(
          searchQuery.toLowerCase()
        )
    );
    setResults(filteredResults);
  };

  const handleReset = () => {
    setSearchQuery("");
    // 重新获取所有结果
    if (location.state?.matchingResults) {
      setResults(location.state.matchingResults);
      generateGraphData(location.state.matchingResults);
    } else {
      fetchMatchingResults();
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* 顶部导航栏 */}
      <div
        className="App-header"
        style={{ height: "auto", minHeight: "auto", padding: "0" }}
      >
        <nav>
          <div className="logo">
            <img src={logo} alt="logo" />
            <span>Cause-connect</span>
          </div>
          <div className="nav-links">
            <a href="#">Explore</a>
            <Link to="/partnership-search">Partnership</Link>
            <a href="#">Solutions</a>
            <div className="profile-menu-container relative">
              <div
                className="flex items-center gap-1 ml-4 cursor-pointer"
                onClick={() => setIsProfileMenuOpen(!isProfileMenuOpen)}
              >
                <div className="w-8 h-8 rounded-full bg-gray-100 flex items-center justify-center">
                  <svg
                    className="w-5 h-5 text-gray-600"
                    fill="none"
                    stroke="currentColor"
                    viewBox="0 0 24 24"
                  >
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      strokeWidth="2"
                      d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z"
                    />
                  </svg>
                </div>
                <span className="text-sm text-gray-700 font-medium">
                  {user?.orgName}
                </span>
                <svg
                  className={`w-4 h-4 text-gray-500 transition-transform duration-200 ${
                    isProfileMenuOpen ? "transform rotate-180" : ""
                  }`}
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth="2"
                    d="M19 9l-7 7-7-7"
                  />
                </svg>
              </div>

              {/* 下拉菜单 */}
              {isProfileMenuOpen && (
                <div className="absolute right-0 top-full mt-1 w-48 rounded-md shadow-lg bg-white ring-1 ring-black ring-opacity-5 z-50">
                  <div className="py-1">
                    <a
                      href="#"
                      className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
                    >
                      Profile Settings
                    </a>
                    <button
                      onClick={() => {
                        localStorage.removeItem("token");
                        window.location.href = "/";
                      }}
                      className="block w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
                    >
                      Sign Out
                    </button>
                  </div>
                </div>
              )}
            </div>
          </div>
        </nav>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="mb-6">
          <h1 className="text-2xl font-semibold text-gray-900">
            Partnerships for you
          </h1>
          {searchParams && Object.keys(searchParams).length > 0 && (
            <div className="mt-2 text-sm text-gray-500">
              <span>Search criteria: </span>
              {searchParams.location && (
                <span className="mr-2">Location: {searchParams.location}</span>
              )}
              {searchParams.organizationType && (
                <span className="mr-2">
                  Organization Type: {searchParams.organizationType}
                </span>
              )}
            </div>
          )}
        </div>

        <div className="flex flex-col md:flex-row gap-8">
          {/* 左侧搜索和结果列表 */}
          <div className="w-full md:w-1/2">
            {/* 搜索和过滤器 */}
            <div className="space-y-4 mb-6">
              <form onSubmit={handleSearch} className="flex gap-4">
                <input
                  type="text"
                  placeholder="Search key words"
                  className="flex-1 px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-teal-500 focus:border-transparent"
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                />
                <button
                  type="submit"
                  className="px-4 py-2 bg-teal-500 text-white rounded-lg hover:bg-teal-600"
                >
                  Search
                </button>
                <button
                  type="button"
                  onClick={handleReset}
                  className="px-4 py-2 bg-gray-500 text-white rounded-lg hover:bg-gray-600"
                >
                  Reset
                </button>
              </form>
              <div className="flex gap-4">
                <select
                  className="px-4 py-2 bg-gray-100 text-gray-700 rounded-lg hover:bg-gray-200"
                  value={sortBy}
                  onChange={(e) => {
                    setSortBy(e.target.value);
                    // 实现排序逻辑
                    const sortedResults = [...results].sort((a, b) => {
                      if (e.target.value === "match") {
                        return (b.matchScore || 0) - (a.matchScore || 0);
                      } else if (e.target.value === "name") {
                        return (a.name || a.orgName || "").localeCompare(
                          b.name || b.orgName || ""
                        );
                      } else if (e.target.value === "location") {
                        return (a.location || "").localeCompare(
                          b.location || ""
                        );
                      }
                      return 0;
                    });
                    setResults(sortedResults);
                  }}
                >
                  <option value="match">Sort by Match</option>
                  <option value="name">Sort by Name</option>
                  <option value="location">Sort by Location</option>
                </select>
              </div>
            </div>

            {/* 错误提示 */}
            {error && (
              <div className="mb-4 p-3 bg-red-100 text-red-700 rounded-lg">
                {error}
              </div>
            )}

            {/* 加载状态 */}
            {isLoading ? (
              <div className="flex justify-center items-center h-64">
                <svg
                  className="animate-spin h-10 w-10 text-teal-500"
                  xmlns="http://www.w3.org/2000/svg"
                  fill="none"
                  viewBox="0 0 24 24"
                >
                  <circle
                    className="opacity-25"
                    cx="12"
                    cy="12"
                    r="10"
                    stroke="currentColor"
                    strokeWidth="4"
                  ></circle>
                  <path
                    className="opacity-75"
                    fill="currentColor"
                    d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
                  ></path>
                </svg>
              </div>
            ) : (
              <>
                {/* 结果列表 */}
                {results.length === 0 ? (
                  <div className="text-center py-10">
                    <p className="text-gray-500">
                      No matching partners found. Try adjusting your search
                      criteria.
                    </p>
                  </div>
                ) : (
                  <div className="space-y-4">
                    {results.map((result) => (
                      <div
                        key={
                          result.id ||
                          result._id ||
                          Math.random().toString(36).substr(2, 9)
                        }
                        className="bg-white p-6 rounded-xl shadow-sm hover:shadow-md transition-shadow"
                      >
                        <div className="flex justify-between items-start">
                          <div>
                            <h3 className="text-xl font-semibold text-gray-900">
                              {result.name || result.orgName}
                            </h3>
                            <p className="text-gray-500">{result.location}</p>
                            <div className="flex gap-2 mt-2">
                              {result.matchCategory && (
                                <span
                                  className="px-2 py-1 bg-gray-100 text-gray-600 text-sm rounded"
                                  style={{
                                    backgroundColor: `${getColorByCategory(
                                      result.matchCategory
                                    )}20`,
                                    color: getColorByCategory(
                                      result.matchCategory
                                    ),
                                  }}
                                >
                                  {result.matchCategory}
                                </span>
                              )}
                              {result.industry && (
                                <span className="px-2 py-1 bg-gray-100 text-gray-600 text-sm rounded">
                                  {result.industry}
                                </span>
                              )}
                            </div>
                            {result.website && (
                              <a
                                href={result.website}
                                target="_blank"
                                rel="noopener noreferrer"
                                className="mt-2 inline-block text-teal-500 hover:text-teal-700"
                              >
                                Visit Website
                              </a>
                            )}
                          </div>
                          <div className="text-2xl font-bold text-teal-500">
                            {result.matchScore ? `${result.matchScore}%` : ""}
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </>
            )}
          </div>

          {/* 右侧网络图 */}
          <div className="w-full md:w-1/2 bg-white rounded-xl shadow-sm p-4 mt-6 md:mt-0">
            <h2 className="text-lg font-semibold mb-4">Network Map</h2>
            <div className="h-[600px]">
              {graphData.nodes.length > 0 ? (
                <ForceGraph2D
                  graphData={graphData}
                  nodeColor={(node) => node.color}
                  nodeLabel={(node) =>
                    `${node.name}${
                      node.matchCategory ? ` (${node.matchCategory})` : ""
                    }`
                  }
                  linkWidth={(link) => link.value * 3}
                  linkColor={() => "#ddd"}
                  nodeRelSize={(node) => (node.id === "current-user" ? 10 : 6)}
                  backgroundColor="#ffffff"
                />
              ) : (
                <div className="flex justify-center items-center h-full">
                  <p className="text-gray-500">
                    No data available for network visualization
                  </p>
                </div>
              )}
            </div>
            <div className="mt-4 flex flex-wrap justify-end gap-4">
              <div className="flex items-center gap-2">
                <span className="w-3 h-3 rounded-full bg-[#4CAF50]"></span>
                <span className="text-sm text-gray-600">🌟 极佳匹配</span>
              </div>
              <div className="flex items-center gap-2">
                <span className="w-3 h-3 rounded-full bg-[#2196F3]"></span>
                <span className="text-sm text-gray-600">✅ 良好匹配</span>
              </div>
              <div className="flex items-center gap-2">
                <span className="w-3 h-3 rounded-full bg-[#FFC107]"></span>
                <span className="text-sm text-gray-600">🟡 一般匹配</span>
              </div>
              <div className="flex items-center gap-2">
                <span className="w-3 h-3 rounded-full bg-[#FF5722]"></span>
                <span className="text-sm text-gray-600">🔴 低匹配</span>
              </div>
              <div className="flex items-center gap-2">
                <span className="w-3 h-3 rounded-full bg-[#9E9E9E]"></span>
                <span className="text-sm text-gray-600">⚫️ 不匹配</span>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
