export default function ProfileDetailsPage() {
  const navigate = useNavigate();
  const { user } = useAuth();
  const [error, setError] = useState("");
  const [formData, setFormData] = useState({
    orgName: "",
    location: "",
    homepage: "",
    coreValues: "",
    fieldOfInterest: "",
    partnershipGoal: "",
    resourcesProvided: "",
  });

  const [selectedState, setSelectedState] = useState("");
  const [availableCities, setAvailableCities] = useState([]);

  useEffect(() => {
    const orgType = localStorage.getItem("orgType");
    if (!orgType) {
      navigate("/profile-setup/type");
    }
  }, [navigate]);

  useEffect(() => {
    if (selectedState && CITIES_BY_STATE[selectedState]) {
      setAvailableCities(CITIES_BY_STATE[selectedState]);
    } else {
      setAvailableCities([]);
    }
    setFormData((prev) => ({
      ...prev,
      location: selectedState ? `${selectedState} - ` : "",
    }));
  }, [selectedState]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    console.log("开始提交表单数据...");

    // 验证必填字段
    if (!formData.orgName.trim()) {
      setError("Organization Name is required");
      return;
    }

    // 验证位置
    if (!selectedState || !formData.location) {
      setError("Both state and city are required");
      return;
    }

    // 验证其他必填字段
    if (!formData.homepage) {
      setError("Organization homepage is required");
      return;
    }
    if (!formData.fieldOfInterest) {
      setError("Field of interest is required");
      return;
    }
    if (!formData.partnershipGoal) {
      setError("Partnership goal is required");
      return;
    }
    if (!formData.resourcesProvided) {
      setError("Resources provided is required");
      return;
    }

    try {
      // 从 token 中获取 userId
      const token = localStorage.getItem("token");
      const tokenPayload = JSON.parse(atob(token.split(".")[1]));
      const userId = tokenPayload.userId;

      if (!userId) {
        setError("User ID not found. Please try logging in again.");
        return;
      }

      const [city] = formData.location.split(" - ").slice(1);

      // 准备发送的数据，严格按照后端 Schema 格式
      const profileData = {
        userId: userId,
        orgType: localStorage.getItem("orgType") || "nonprofit", // 确保有默认值
        orgName: formData.orgName.trim(),
        location: {
          state: selectedState,
          city: city || selectedState, // 确保有值
        },
        homepage: formData.homepage.trim(),
        coreValues: formData.coreValues
          ? formData.coreValues
              .split(",")
              .map((v) => v.trim())
              .filter(Boolean)
              .slice(0, 3)
          : [],
        focusField: formData.fieldOfInterest,
        partnershipGoal: formData.partnershipGoal.trim(),
        resourcesProvided: formData.resourcesProvided.trim(),
      };

      // 打印完整的请求信息以便调试
      console.log("完整请求信息:", {
        url: "http://localhost:3001/api/profile",
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: profileData,
      });

      const response = await fetch("http://localhost:3001/api/profile", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(profileData),
      });

      if (!response.ok) {
        const errorText = await response.text();
        console.error("服务器错误响应:", errorText);
        try {
          const errorData = JSON.parse(errorText);
          throw new Error(errorData.message || "Failed to save profile");
        } catch (e) {
          throw new Error("Server error: " + errorText);
        }
      }

      const data = await response.json();
      console.log("保存成功，服务器响应:", data);

      // 更新 AuthContext 中的用户信息
      if (user) {
        user.orgName = formData.orgName;
      }

      // 跳转到主页
      navigate("/home");
    } catch (error) {
      console.error("错误详情:", error);
      setError(error.message || "An error occurred while saving the profile");
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name === "state") {
      setSelectedState(value);
    } else if (name === "city") {
      setFormData((prev) => ({
        ...prev,
        location: `${selectedState} - ${value}`,
      }));
    } else {
      setFormData((prev) => ({
        ...prev,
        [name]: value,
      }));
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center px-4 sm:px-6 lg:px-8">
      <div className="max-w-lg w-full space-y-8">
        <div>
          <h2 className="mt-6 text-center text-3xl font-bold text-gray-900">
            Organization Details
          </h2>
          <p className="mt-2 text-center text-sm text-gray-600">
            Help us understand your organization better
          </p>
        </div>

        {error && <div className="mt-4 text-red-600">{error}</div>}

        <form onSubmit={handleSubmit} className="mt-8 space-y-6">
          <div>
            <label
              htmlFor="orgName"
              className="block text-sm font-medium text-gray-700"
            >
              Organization Name <span className="text-red-500">*</span>
            </label>
            <input
              type="text"
              name="orgName"
              id="orgName"
              required
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-gray-500 focus:ring-gray-500"
              value={formData.orgName}
              onChange={handleChange}
              placeholder="Enter your organization name"
            />
            <p className="mt-1 text-xs text-gray-500">
              This name will be displayed on your profile
            </p>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700">
              Location <span className="text-gray-400">(Optional)</span>
            </label>
            <div className="mt-1 grid grid-cols-2 gap-4">
              <div>
                <select
                  name="state"
                  value={selectedState}
                  onChange={handleChange}
                  className="block w-full rounded-md border-gray-300 shadow-sm focus:border-gray-500 focus:ring-gray-500"
                >
                  <option value="">Select State</option>
                  {US_STATES.map((state) => (
                    <option key={state} value={state}>
                      {state}
                    </option>
                  ))}
                </select>
              </div>
              <div>
                <select
                  name="city"
                  onChange={handleChange}
                  disabled={!selectedState}
                  className="block w-full rounded-md border-gray-300 shadow-sm focus:border-gray-500 focus:ring-gray-500"
                >
                  <option value="">Select City</option>
                  {availableCities.map((city) => (
                    <option key={city} value={city}>
                      {city}
                    </option>
                  ))}
                </select>
              </div>
            </div>
          </div>

          <div>
            <label
              htmlFor="homepage"
              className="block text-sm font-medium text-gray-700"
            >
              Organization Homepage{" "}
              <span className="text-gray-400">(Optional)</span>
            </label>
            <input
              type="url"
              name="homepage"
              id="homepage"
              placeholder="https://www.example.com"
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-gray-500 focus:ring-gray-500"
              value={formData.homepage}
              onChange={handleChange}
            />
          </div>

          <div>
            <label
              htmlFor="coreValues"
              className="block text-sm font-medium text-gray-700"
            >
              Top 3 Core Values{" "}
              <span className="text-gray-400">(Optional)</span>
            </label>
            <input
              type="text"
              name="coreValues"
              id="coreValues"
              placeholder="Sustainability, Innovation, Community"
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-gray-500 focus:ring-gray-500"
              value={formData.coreValues}
              onChange={handleChange}
            />
          </div>

          <div>
            <label
              htmlFor="fieldOfInterest"
              className="block text-sm font-medium text-gray-700"
            >
              Field of Interest{" "}
              <span className="text-gray-400">(Optional)</span>
            </label>
            <select
              name="fieldOfInterest"
              id="fieldOfInterest"
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-gray-500 focus:ring-gray-500"
              value={formData.fieldOfInterest}
              onChange={handleChange}
            >
              <option value="">Select a field</option>
              {FIELDS_OF_INTEREST.map((field) => (
                <option key={field} value={field}>
                  {field}
                </option>
              ))}
            </select>
          </div>

          <div>
            <label
              htmlFor="partnershipGoal"
              className="block text-sm font-medium text-gray-700"
            >
              Co-Marketing Partnership Goal{" "}
              <span className="text-gray-400">(Optional)</span>
            </label>
            <textarea
              name="partnershipGoal"
              id="partnershipGoal"
              rows={3}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-gray-500 focus:ring-gray-500"
              value={formData.partnershipGoal}
              onChange={handleChange}
              placeholder="What do you hope to achieve through partnerships?"
            />
          </div>

          <div>
            <label
              htmlFor="resourcesProvided"
              className="block text-sm font-medium text-gray-700"
            >
              Resources Provided{" "}
              <span className="text-gray-400">(Optional)</span>
            </label>
            <textarea
              name="resourcesProvided"
              id="resourcesProvided"
              rows={3}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-gray-500 focus:ring-gray-500"
              value={formData.resourcesProvided}
              onChange={handleChange}
              placeholder="What resources can you offer to potential partners?"
            />
          </div>

          <div>
            <button
              type="submit"
              className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-gray-800 hover:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gray-500"
            >
              Complete Profile
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}
