import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import heroImage from "../images/hero4.jpg";
import { useAuth } from "../context/AuthContext";

const PartnershipSearch = () => {
  const navigate = useNavigate();
  const { user } = useAuth();
  const [formData, setFormData] = useState({
    location: "",
    organizationType: "",
    partnershipGoal: "",
  });
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState("");
  const [profileLoaded, setProfileLoaded] = useState(false);

  // 组件加载时检查用户是否已经填写过个人资料
  useEffect(() => {
    const checkUserProfile = async () => {
      if (!user) {
        navigate("/signin");
        return;
      }

      try {
        const token = localStorage.getItem("token");
        if (!token) {
          throw new Error("未登录，请先登录");
        }

        // 获取用户之前的匹配偏好
        const response = await fetch(
          "http://localhost:3001/api/profile/matching-preference",
          {
            method: "GET",
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );

        if (response.ok) {
          const data = await response.json();

          // 如果用户已经有匹配偏好，则自动填充表单
          if (data.preference) {
            setFormData({
              location: data.preference.location || "",
              organizationType: data.preference.targetOrgType || "",
              partnershipGoal: data.preference.partnershipGoal || "",
            });
            setProfileLoaded(true);
          }
        }
      } catch (error) {
        console.error("获取用户匹配偏好错误:", error);
        // 这里不设置错误状态，因为这只是一个预加载操作
      }
    };

    checkUserProfile();
  }, [user, navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError("");

    try {
      // 获取 token
      const token = localStorage.getItem("token");
      if (!token) {
        throw new Error("未登录，请先登录");
      }

      // 如果用户已经填写过个人资料，并且没有修改任何内容，可以直接跳转到结果页面
      if (
        profileLoaded &&
        formData.location === document.getElementById("location").value &&
        formData.organizationType ===
          document.getElementById("organizationType").value &&
        formData.partnershipGoal ===
          document.getElementById("partnershipGoal").value
      ) {
        // 直接获取匹配结果
        const resultsResponse = await fetch(
          "http://localhost:3001/api/profile/matching-results",
          {
            method: "GET",
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );

        if (!resultsResponse.ok) {
          const errorData = await resultsResponse.json();
          throw new Error(errorData.message || "获取匹配结果失败");
        }

        const resultsData = await resultsResponse.json();

        // 导航到结果页面，并传递搜索参数和结果
        navigate("/results", {
          state: {
            searchParams: formData,
            matchingResults: resultsData.results || [],
          },
        });
        return;
      }

      // 发送请求到 API
      const response = await fetch(
        "http://localhost:3001/api/profile/matching-preference",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify({
            location: formData.location,
            targetOrgType: formData.organizationType,
            partnershipGoal: formData.partnershipGoal,
          }),
        }
      );

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || "保存匹配偏好失败");
      }

      console.log("匹配偏好保存成功:", data);

      // 导航到结果页面，并传递搜索参数
      navigate("/results", {
        state: {
          searchParams: formData,
          matchingResults: data.results || [],
        },
      });
    } catch (error) {
      console.error("保存匹配偏好错误:", error);
      setError(error.message || "提交表单时出错");
    } finally {
      setIsLoading(false);
    }
  };

  return (
