import { useState, useEffect } from "react";
import { useNavigate, useLocation, Link } from "react-router-dom";
import ForceGraph2D from "react-force-graph-2d";
import { useAuth } from "../context/AuthContext";
import logo from "../images/logo.svg";

// API Âü∫Á°Ä URL
const API_BASE_URL = "http://localhost:3001/api";

export default function ResultPage() {
  const navigate = useNavigate();
  const location = useLocation();
  const { user } = useAuth();
  const [isProfileMenuOpen, setIsProfileMenuOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [sortBy, setSortBy] = useState("match");
  const [results, setResults] = useState([]);
  const [graphData, setGraphData] = useState({ nodes: [], links: [] });
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState("");
  const [searchParams, setSearchParams] = useState(
    location.state?.searchParams || {}
  );

  // Ê∑ªÂä†ÁÇπÂáªÂ§ñÈÉ®ÂÖ≥Èó≠ËèúÂçïÁöÑÂ§ÑÁêÜÂáΩÊï∞
  useEffect(() => {
    const closeMenu = (e) => {
      if (isProfileMenuOpen && !e.target.closest(".profile-menu-container")) {
        setIsProfileMenuOpen(false);
      }
    };

    document.addEventListener("click", closeMenu);
    return () => document.removeEventListener("click", closeMenu);
  }, [isProfileMenuOpen]);

  useEffect(() => {
    // Â¶ÇÊûú‰ªé location ‰º†ÈÄí‰∫ÜÈîôËØØ‰ø°ÊÅØÔºåÊòæÁ§∫ÈîôËØØ
    if (location.state?.error) {
      setError(location.state.error);
    }

    // ‰øùÂ≠òÊêúÁ¥¢ÂèÇÊï∞
    if (location.state?.searchParams) {
      setSearchParams(location.state.searchParams);
    }

    // Â¶ÇÊûú‰ªé location ‰º†ÈÄí‰∫ÜÂåπÈÖçÁªìÊûúÔºå‰ΩøÁî®ÂÆÉ‰ª¨
    if (
      location.state?.matchingResults &&
      location.state.matchingResults.length > 0
    ) {
      console.log(
        "‰ΩøÁî®‰ªé location ‰º†ÈÄíÁöÑÂåπÈÖçÁªìÊûú:",
        location.state.matchingResults
      );
      setResults(location.state.matchingResults);
      generateGraphData(location.state.matchingResults);
    } else {
      // Âê¶Âàô‰ªé API Ëé∑ÂèñÂåπÈÖçÁªìÊûú
      fetchMatchingResults();
    }
  }, [location]);

  const fetchMatchingResults = async () => {
    setIsLoading(true);
    setError("");
    setResults([]);
    setGraphData({ nodes: [], links: [] });

    try {
      const token = localStorage.getItem("token");
      if (!token) {
        throw new Error("Êú™ÁôªÂΩïÔºåËØ∑ÂÖàÁôªÂΩï");
      }

      if (!user || (!user._id && !user.id)) {
        console.error("Áî®Êà∑‰ø°ÊÅØ‰∏çÂÆåÊï¥:", user);
        throw new Error("Êó†Ê≥ïËé∑ÂèñÁî®Êà∑‰ø°ÊÅØÔºåËØ∑ÈáçÊñ∞ÁôªÂΩï");
      }

      const userId = user._id || user.id;
      console.log("ÂΩìÂâçÁî®Êà∑ID:", userId);

      // Â∞ùËØï‰ªé API Ëé∑ÂèñÂåπÈÖçÁªìÊûú
      console.log("Â∞ùËØï‰ªé API Ëé∑ÂèñÂåπÈÖçÁªìÊûú...");

      // È¶ñÂÖàÂ∞ùËØï profile/matching-results Á´ØÁÇπ
      try {
        const response = await fetch(
          `${API_BASE_URL}/profile/matching-results`,
          {
            method: "GET",
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );

        if (!response.ok) {
          console.warn(
            "‰ªé matching-results Á´ØÁÇπËé∑ÂèñÂ§±Ë¥•ÔºåÁä∂ÊÄÅÁ†Å:",
            response.status
          );

          // Â¶ÇÊûúÁ¨¨‰∏Ä‰∏™Á´ØÁÇπÂ§±Ë¥•ÔºåÂ∞ùËØï match_partners Á´ØÁÇπ
          const alternativeResponse = await fetch(
            `${API_BASE_URL}/match_partners?user_id=${userId}`,
            {
              headers: {
                Authorization: `Bearer ${token}`,
              },
            }
          );

          if (!alternativeResponse.ok) {
            console.warn(
              "‰ªé match_partners Á´ØÁÇπËé∑ÂèñÂ§±Ë¥•ÔºåÁä∂ÊÄÅÁ†Å:",
              alternativeResponse.status
            );
            throw new Error("Êó†Ê≥ïËé∑ÂèñÂåπÈÖçÁªìÊûúÔºåËØ∑Á®çÂêéÂÜçËØï");
          }

          const alternativeData = await alternativeResponse.json();
          console.log("‰ªé match_partners Ëé∑ÂèñÁöÑÂåπÈÖçÁªìÊûú:", alternativeData);

          if (alternativeData.results && alternativeData.results.length > 0) {
            setResults(alternativeData.results);
            generateGraphData(alternativeData.results);
          } else {
            setError("Êú™ÊâæÂà∞ÂåπÈÖçÁªìÊûú");
          }
          return;
        }

        const data = await response.json();
        console.log("‰ªé matching-results Ëé∑ÂèñÁöÑÂåπÈÖçÁªìÊûú:", data);

        if (data.results && data.results.length > 0) {
          setResults(data.results);
          generateGraphData(data.results);
        } else {
          setError("Êú™ÊâæÂà∞ÂåπÈÖçÁªìÊûú");
        }
      } catch (apiError) {
        console.error("API ËØ∑Ê±ÇÈîôËØØ:", apiError);
        throw apiError;
      }
    } catch (error) {
      console.error("Ëé∑ÂèñÂåπÈÖçÁªìÊûúÈîôËØØ:", error);
      setError(error.message || "Ëé∑ÂèñÂåπÈÖçÁªìÊûúÊó∂Âá∫Èîô");
    } finally {
      setIsLoading(false);
    }
  };

  const generateGraphData = (matchResults) => {
    if (!matchResults || matchResults.length === 0) {
      setGraphData({ nodes: [], links: [] });
      return;
    }

    // Â§ÑÁêÜÂåπÈÖçÁªìÊûúÔºåÊ∑ªÂä†ÂåπÈÖçÁ±ªÂà´
    const processedResults = matchResults.map((result) => {
      // Â¶ÇÊûúÁªìÊûú‰∏≠Â∑≤ÁªèÊúâ matchCategoryÔºåÁõ¥Êé•‰ΩøÁî®
      if (result.matchCategory) {
        return result;
      }

      // Âê¶ÂàôÔºåÊ†πÊçÆÂåπÈÖçÂàÜÊï∞ÁîüÊàêÂåπÈÖçÁ±ªÂà´
      const score = result.matchScore || 0;
      let matchCategory;

      if (score >= 90) {
        matchCategory = "üåü ÊûÅ‰Ω≥ÂåπÈÖç";
      } else if (score >= 75) {
        matchCategory = "‚úÖ ËâØÂ•ΩÂåπÈÖç";
      } else if (score >= 50) {
        matchCategory = "üü° ‰∏ÄËà¨ÂåπÈÖç";
      } else if (score >= 25) {
        matchCategory = "üî¥ ‰ΩéÂåπÈÖç";
      } else {
        matchCategory = "‚ö´Ô∏è ‰∏çÂåπÈÖç";
      }

      return { ...result, matchCategory };
    });

    // Êõ¥Êñ∞Â§ÑÁêÜÂêéÁöÑÁªìÊûú
    setResults(processedResults);

    // ÁîüÊàêÁΩëÁªúÂõæÊï∞ÊçÆ
    const nodes = processedResults.map((result) => ({
      id: result.id || result._id,
      name: result.name || result.orgName,
      matchCategory: result.matchCategory,
      score: result.matchScore,
      color: getColorByCategory(result.matchCategory),
    }));

    // Ê∑ªÂä†ÂΩìÂâçÁî®Êà∑ËäÇÁÇπ
    nodes.push({
      id: "current-user",
      name: user?.orgName || "Your Organization",
      color: "#4CAF50",
    });

    // ÁîüÊàêËøûÊé• - ÊâÄÊúâÁªÑÁªáÈÉΩ‰∏éÂΩìÂâçÁî®Êà∑Áõ∏Ëøû
    const links = nodes
      .filter((node) => node.id !== "current-user")
      .map((node) => ({
        source: "current-user",
        target: node.id,
        value: (node.score || 50) / 100,
      }));

    setGraphData({ nodes, links });
  };

  const getColorByCategory = (category) => {
    const colorMap = {
      "üåü ÊûÅ‰Ω≥ÂåπÈÖç": "#4CAF50",
      "‚úÖ ËâØÂ•ΩÂåπÈÖç": "#2196F3",
      "üü° ‰∏ÄËà¨ÂåπÈÖç": "#FFC107",
      "üî¥ ‰ΩéÂåπÈÖç": "#FF5722",
      "‚ö´Ô∏è ‰∏çÂåπÈÖç": "#9E9E9E",
    };
    return colorMap[category] || "#9E9E9E";
  };

  const handleSearch = (e) => {
    e.preventDefault();
    // ÂÆûÁé∞ÊêúÁ¥¢ÂäüËÉΩ
    const filteredResults = results.filter(
      (result) =>
        (
          result.name?.toLowerCase() ||
          result.orgName?.toLowerCase() ||
          ""
        ).includes(searchQuery.toLowerCase()) ||
        (result.location?.toLowerCase() || "").includes(
          searchQuery.toLowerCase()
        )
    );
    setResults(filteredResults);
  };

  const handleReset = () => {
    setSearchQuery("");
    // ÈáçÊñ∞Ëé∑ÂèñÊâÄÊúâÁªìÊûú
    if (location.state?.matchingResults) {
      setResults(location.state.matchingResults);
      generateGraphData(location.state.matchingResults);
    } else {
      fetchMatchingResults();
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* È°∂ÈÉ®ÂØºËà™Ê†è */}
      <div
        className="App-header"
        style={{ height: "auto", minHeight: "auto", padding: "0" }}
      >
        <nav>
          <div className="logo">
            <img src={logo} alt="logo" />
            <span>Cause-connect</span>
          </div>
          <div className="nav-links">
            <a href="#">Explore</a>
            <Link to="/partnership-search">Partnership</Link>
            <a href="#">Solutions</a>
            <div className="profile-menu-container relative">
              <div
                className="flex items-center gap-1 ml-4 cursor-pointer"
                onClick={() => setIsProfileMenuOpen(!isProfileMenuOpen)}
              >
                <div className="w-8 h-8 rounded-full bg-gray-100 flex items-center justify-center">
                  <svg
                    className="w-5 h-5 text-gray-600"
                    fill="none"
                    stroke="currentColor"
                    viewBox="0 0 24 24"
                  >
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      strokeWidth="2"
                      d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z"
                    />
                  </svg>
                </div>
                <span className="text-sm text-gray-700 font-medium">
                  {user?.orgName}
                </span>
                <svg
                  className={`w-4 h-4 text-gray-500 transition-transform duration-200 ${
                    isProfileMenuOpen ? "transform rotate-180" : ""
                  }`}
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth="2"
                    d="M19 9l-7 7-7-7"
                  />
                </svg>
              </div>

              {/* ‰∏ãÊãâËèúÂçï */}
              {isProfileMenuOpen && (
                <div className="absolute right-0 top-full mt-1 w-48 rounded-md shadow-lg bg-white ring-1 ring-black ring-opacity-5 z-50">
                  <div className="py-1">
                    <a
                      href="#"
                      className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
                    >
                      Profile Settings
                    </a>
                    <button
                      onClick={() => {
                        localStorage.removeItem("token");
                        window.location.href = "/";
                      }}
                      className="block w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
                    >
                      Sign Out
                    </button>
                  </div>
                </div>
              )}
            </div>
          </div>
        </nav>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="mb-6">
          <h1 className="text-2xl font-semibold text-gray-900">
            Partnerships for you
          </h1>
          {searchParams && Object.keys(searchParams).length > 0 && (
            <div className="mt-2 text-sm text-gray-500">
              <span>Search criteria: </span>
              {searchParams.location && (
                <span className="mr-2">Location: {searchParams.location}</span>
