          <div className="w-full md:w-1/2">
            {/* ÊêúÁ¥¢ÂíåËøáÊª§Âô® */}
            {results.length > 0 && (
              <div className="space-y-4 mb-6">
                <form onSubmit={handleSearch} className="flex gap-4">
