                        className="bg-white p-6 rounded-xl shadow-sm hover:shadow-md transition-shadow"
                      >
                        <div className="flex justify-between items-start">
                          <div>
                            <h3 className="text-xl font-semibold text-gray-900">
                              {result.name || result.orgName}
                            </h3>
                            <p className="text-gray-500">{result.location}</p>
                            <div className="flex gap-2 mt-2">
                              {result.matchCategory && (
                                <span
                                  className="px-2 py-1 bg-gray-100 text-gray-600 text-sm rounded"
                                  style={{
                                    backgroundColor: `${getColorByCategory(
                                      result.matchCategory
                                    )}20`,
                                    color: getColorByCategory(
                                      result.matchCategory
                                    ),
                                  }}
                                >
                                  {result.matchCategory}
                                </span>
                              )}
                              {result.industry && (
                                <span className="px-2 py-1 bg-gray-100 text-gray-600 text-sm rounded">
                                  {result.industry}
                                </span>
                              )}
                            </div>
                            {result.website && (
                              <a
                                href={result.website}
                                target="_blank"
                                rel="noopener noreferrer"
                                className="mt-2 inline-block text-teal-500 hover:text-teal-700"
                              >
                                Visit Website
                              </a>
                            )}
                          </div>
                          <div className="text-2xl font-bold text-teal-500">
                            {result.matchScore ? `${result.matchScore}%` : ""}
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </>
            )}
          </div>

          {/* Âè≥‰æßÁΩëÁªúÂõæ */}
          <div className="w-full md:w-1/2 bg-white rounded-xl shadow-sm p-4 mt-6 md:mt-0">
            <h2 className="text-lg font-semibold mb-4">Network Map</h2>
            <div className="h-[600px]">
              {graphData.nodes.length > 0 ? (
                <ForceGraph2D
                  graphData={graphData}
                  nodeColor={(node) => node.color}
                  nodeLabel={(node) =>
                    `${node.name}${
                      node.matchCategory ? ` (${node.matchCategory})` : ""
                    }`
                  }
                  linkWidth={(link) => link.value * 3}
                  linkColor={() => "#ddd"}
