import { useState, useEffect } from "react";
import { useNavigate, useLocation, Link } from "react-router-dom";
import ForceGraph2D from "react-force-graph-2d";
import { useAuth } from "../context/AuthContext";
import logo from "../images/logo.svg";

// API åŸºç¡€ URL
const API_BASE_URL = "http://localhost:3001/api";

export default function ResultPage() {
  const navigate = useNavigate();
  const location = useLocation();
  const { user } = useAuth();
  const [isProfileMenuOpen, setIsProfileMenuOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [sortBy, setSortBy] = useState("match");
  const [results, setResults] = useState([]);
  const [graphData, setGraphData] = useState({ nodes: [], links: [] });
  const [isLoading, setIsLoading] = useState(true); // é»˜è®¤ä¸ºåŠ è½½çŠ¶æ€
  const [error, setError] = useState("");
  const [searchParams, setSearchParams] = useState(
    location.state?.searchParams || {}
  );
  const [dataSource, setDataSource] = useState(""); // ç”¨äºè·Ÿè¸ªæ•°æ®æ¥æº
  const [retryCount, setRetryCount] = useState(0); // ç”¨äºè·Ÿè¸ªé‡è¯•æ¬¡æ•°

  // æ·»åŠ ç‚¹å‡»å¤–éƒ¨å…³é—­èœå•çš„å¤„ç†å‡½æ•°
  useEffect(() => {
    const closeMenu = (e) => {
      if (isProfileMenuOpen && !e.target.closest(".profile-menu-container")) {
        setIsProfileMenuOpen(false);
      }
    };

    document.addEventListener("click", closeMenu);
    return () => document.removeEventListener("click", closeMenu);
  }, [isProfileMenuOpen]);

  // æ£€æŸ¥ç”¨æˆ·ç™»å½•çŠ¶æ€å¹¶è·å–ç”¨æˆ·ä¿¡æ¯
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      setError("è¯·å…ˆç™»å½•");
      setIsLoading(false);
      return;
    }

    // æ£€æŸ¥ç”¨æˆ·ä¿¡æ¯æ˜¯å¦å®Œæ•´
    if (!user) {
      console.log("ç”¨æˆ·ä¿¡æ¯ä¸å®Œæ•´ï¼Œç­‰å¾…1ç§’åé‡è¯•...");

      // å¦‚æœé‡è¯•æ¬¡æ•°å°‘äº3æ¬¡ï¼Œè®¾ç½®å®šæ—¶å™¨é‡è¯•
      if (retryCount < 3) {
        const timer = setTimeout(() => {
          setRetryCount((prev) => prev + 1);
        }, 1000); // ç­‰å¾…1ç§’åé‡è¯•

        return () => clearTimeout(timer);
      } else {
        setError("æ— æ³•è·å–ç”¨æˆ·ä¿¡æ¯ï¼Œè¯·é‡æ–°ç™»å½•");
        setIsLoading(false);
      }
    } else {
      console.log("ç”¨æˆ·ä¿¡æ¯:", user);

      // ä¿å­˜æœç´¢å‚æ•°
      if (location.state?.searchParams) {
        setSearchParams(location.state.searchParams);
      }

      // è·å–åŒ¹é…ç»“æœ
      fetchMatchingResults();
    }
  }, [user, retryCount]);

  const fetchMatchingResults = async () => {
    if (!user) {
      setError("æ— æ³•è·å–ç”¨æˆ·ä¿¡æ¯ï¼Œè¯·é‡æ–°ç™»å½•");
      setIsLoading(false);
      return;
    }

    setIsLoading(true);
    setError("");
    setResults([]);
    setGraphData({ nodes: [], links: [] });
    setDataSource("");

    try {
      const token = localStorage.getItem("token");
      if (!token) {
        throw new Error("æœªç™»å½•ï¼Œè¯·å…ˆç™»å½•");
      }

      // è·å–ç”¨æˆ·IDï¼Œç¡®ä¿å®ƒæ˜¯å­˜åœ¨çš„
      let userId = null;
      if (user._id) {
        userId = user._id;
      } else if (user.id) {
        userId = user.id;
      } else if (user.userId) {
        userId = user.userId;
      } else if (user.uid) {
        userId = user.uid;
      }

      if (!userId) {
        console.error("æ— æ³•è·å–ç”¨æˆ·IDï¼Œç”¨æˆ·å¯¹è±¡:", user);
        throw new Error("æ— æ³•è·å–ç”¨æˆ·IDï¼Œè¯·é‡æ–°ç™»å½•");
      }

      console.log("å½“å‰ç”¨æˆ·ID:", userId);
      console.log("å°è¯•ä» API è·å–åŒ¹é…ç»“æœ...");
      console.log("searchParams:", searchParams);

      // å°è¯•ç›´æ¥ä½¿ç”¨ /api/profile/matched-partners ç«¯ç‚¹ (å¦‚æœå­˜åœ¨)
      try {
        console.log("å°è¯•ä» /api/profile/matched-partners è·å–åŒ¹é…ç»“æœ...");
        const directResponse = await fetch(
          `${API_BASE_URL}/profile/matched-partners`,
          {
            method: "GET",
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );

        console.log("matched-partners å“åº”çŠ¶æ€:", directResponse.status);

        if (directResponse.ok) {
          const data = await directResponse.json();
          console.log("ä» matched-partners è·å–çš„åŒ¹é…ç»“æœ:", data);

          if (data.results && data.results.length > 0) {
            setDataSource("matched-partners API");
            setResults(data.results);
            generateGraphData(data.results);
            setIsLoading(false);
            return;
          }
        }
      } catch (directError) {
        console.warn("ä» matched-partners è·å–å¤±è´¥:", directError);
        // ç»§ç»­å°è¯•å…¶ä»–ç«¯ç‚¹
      }

      // é¦–å…ˆå°è¯• profile/matching-results ç«¯ç‚¹
      try {
        const response = await fetch(
          `${API_BASE_URL}/profile/matching-results`,
          {
            method: "GET",
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );

        console.log("matching-results å“åº”çŠ¶æ€:", response.status);

        if (!response.ok) {
          console.warn(
            "ä» matching-results ç«¯ç‚¹è·å–å¤±è´¥ï¼ŒçŠ¶æ€ç :",
            response.status
          );

          // å¦‚æœç¬¬ä¸€ä¸ªç«¯ç‚¹å¤±è´¥ï¼Œå°è¯• match_partners ç«¯ç‚¹
          const alternativeResponse = await fetch(
            `${API_BASE_URL}/match_partners?user_id=${userId}`,
            {
              headers: {
                Authorization: `Bearer ${token}`,
              },
            }
          );

          console.log("match_partners å“åº”çŠ¶æ€:", alternativeResponse.status);

          if (!alternativeResponse.ok) {
            console.warn(
              "ä» match_partners ç«¯ç‚¹è·å–å¤±è´¥ï¼ŒçŠ¶æ€ç :",
              alternativeResponse.status
            );
            // æœ€åä¸€æ¬¡å°è¯•ï¼Œç›´æ¥å‘é€åˆ°åç«¯API - ä½¿ç”¨searchParams
            const finalAttempt = await fetch(
              `${API_BASE_URL}/profile/search-partners`,
              {
                method: "POST",
                headers: {
                  "Content-Type": "application/json",
                  Authorization: `Bearer ${token}`,
