import { useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";
import ForceGraph2D from "react-force-graph-2d";
import { useAuth } from "../context/AuthContext";
import logo from "../images/logo.svg";

export default function ResultPage() {
  const navigate = useNavigate();
  const { user } = useAuth();
  const [isProfileMenuOpen, setIsProfileMenuOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [sortBy, setSortBy] = useState("match");
  const [results, setResults] = useState([]);
  const [graphData, setGraphData] = useState({
    nodes: [],
    links: [],
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  // 添加点击外部关闭菜单的处理函数
  useEffect(() => {
    const closeMenu = (e) => {
      if (isProfileMenuOpen && !e.target.closest(".profile-menu-container")) {
        setIsProfileMenuOpen(false);
      }
    };

    document.addEventListener("click", closeMenu);
    return () => document.removeEventListener("click", closeMenu);
  }, [isProfileMenuOpen]);

  // 获取匹配结果
  useEffect(() => {
    const fetchMatchResults = async () => {
      setLoading(true);
      setError("");

      try {
        const token = localStorage.getItem("token");
        if (!token) {
          navigate("/signin");
          return;
        }

        // 获取匹配需求
        const prefResponse = await fetch(
          "http://localhost:3001/api/profile/matching-preference",
          {
            method: "GET",
            headers: {
              "Content-Type": "application/json",
              Authorization: `Bearer ${token}`,
            },
          }
        );

        const prefData = await prefResponse.json();

        if (!prefResponse.ok) {
          throw new Error(prefData.message || "获取匹配需求失败");
        }

        if (prefData.code !== 0 || !prefData.data) {
          // 如果没有匹配需求，重定向到搜索页面
          navigate("/partnership-search");
          return;
        }

        // 这里应该有一个获取匹配结果的 API 调用
        // 由于您没有提供这个 API，我们暂时使用模拟数据

        // 模拟 API 调用延迟
        await new Promise((resolve) => setTimeout(resolve, 1000));

        // 模拟数据
        const mockResults = [
          {
            id: 1,
            name: "Bellevue Botanical Garden",
            location: "Seattle · Washington",
            matchScore: 98,
            tags: ["Environmental", "Education", "Community"],
          },
          {
            id: 2,
            name: "Seattle Children's Museum",
            location: "Seattle · Washington",
            matchScore: 93,
            tags: ["Education", "Youth", "Arts"],
          },
          {
            id: 3,
            name: "Northwest Harvest",
            location: "Seattle · Washington",
            matchScore: 88,
            tags: ["Food Security", "Community", "Volunteer"],
          },
          {
            id: 4,
            name: "Pacific Science Center",
            location: "Seattle · Washington",
            matchScore: 80,
            tags: ["Science", "Education", "Youth"],
          },
        ];

        setResults(mockResults);

        // 生成网络图数据
        generateGraphData(mockResults);
      } catch (error) {
        console.error("获取匹配结果错误:", error);
        setError(error.message || "获取匹配结果时出错");
      } finally {
        setLoading(false);
      }
    };

    fetchMatchResults();
  }, [navigate]);

  // 生成网络图数据
  const generateGraphData = (results) => {
    const nodes = results.map((result) => ({
      id: result.id,
      name: result.name,
      score: result.matchScore,
      color: getColorByScore(result.matchScore),
    }));

    // 添加当前用户节点
    nodes.push({
      id: "current-user",
      name: user?.orgName || "Your Organization",
      score: 100,
      color: "#4CAF50",
    });

    const links = [];
    // 生成连接 - 所有组织都与当前用户相连
    nodes.forEach((node) => {
      if (node.id !== "current-user") {
        links.push({
          source: "current-user",
          target: node.id,
          value: node.score / 100,
        });
      }
    });

    // 生成组织之间的连接
    nodes.forEach((node, i) => {
      if (node.id === "current-user") return;

      nodes.slice(i + 1).forEach((target) => {
        if (target.id !== "current-user" && Math.random() > 0.5) {
          links.push({
            source: node.id,
            target: target.id,
            value: Math.random() * 0.5, // 组织间连接强度较弱
          });
        }
      });
    });

    setGraphData({ nodes, links });
  };

  const getColorByScore = (score) => {
    if (score >= 80) return "#4CAF50";
    if (score >= 60) return "#FFC107";
    return "#FF5722";
  };

  // 处理搜索
  const handleSearch = () => {
    if (!searchQuery.trim()) return;

    // 根据搜索词过滤结果
    const filteredResults = results.filter(
      (result) =>
        result.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        result.location.toLowerCase().includes(searchQuery.toLowerCase()) ||
        result.tags.some((tag) =>
          tag.toLowerCase().includes(searchQuery.toLowerCase())
        )
    );

    // 更新图表
    generateGraphData(filteredResults);
  };

  // 处理排序
  const handleSort = (e) => {
    const sortValue = e.target.value;
    setSortBy(sortValue);

    let sortedResults = [...results];

    switch (sortValue) {
      case "match":
        sortedResults.sort((a, b) => b.matchScore - a.matchScore);
        break;
      case "name":
        sortedResults.sort((a, b) => a.name.localeCompare(b.name));
        break;
      case "location":
        sortedResults.sort((a, b) => a.location.localeCompare(b.location));
        break;
      default:
        break;
    }

    setResults(sortedResults);
    generateGraphData(sortedResults);
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* 顶部导航栏 */}
      <div
        className="App-header"
        style={{ height: "auto", minHeight: "auto", padding: "0" }}
      >
        <nav>
          <div className="logo">
            <img src={logo} alt="logo" />
            <span>Cause-connect</span>
          </div>
          <div className="nav-links">
            <a href="#">Explore</a>
            <Link to="/partnership-search">Partnership</Link>
            <a href="#">Solutions</a>
            <div className="profile-menu-container relative">
              <div
                className="flex items-center gap-1 ml-4 cursor-pointer"
                onClick={() => setIsProfileMenuOpen(!isProfileMenuOpen)}
              >
                <div className="w-8 h-8 rounded-full bg-gray-100 flex items-center justify-center">
                  <svg
                    className="w-5 h-5 text-gray-600"
                    fill="none"
                    stroke="currentColor"
                    viewBox="0 0 24 24"
                  >
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      strokeWidth="2"
                      d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z"
                    />
                  </svg>
                </div>
                <span className="text-sm text-gray-700 font-medium">
                  {user?.orgName}
                </span>
                <svg
                  className={`w-4 h-4 text-gray-500 transition-transform duration-200 ${
                    isProfileMenuOpen ? "transform rotate-180" : ""
                  }`}
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth="2"
                    d="M19 9l-7 7-7-7"
                  />
                </svg>
              </div>

              {/* 下拉菜单 */}
              {isProfileMenuOpen && (
                <div className="absolute right-0 top-full mt-1 w-48 rounded-md shadow-lg bg-white ring-1 ring-black ring-opacity-5 z-50">
                  <div className="py-1">
                    <a
                      href="#"
                      className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
                    >
                      Profile Settings
                    </a>
                    <button
                      onClick={() => {
                        localStorage.removeItem("token");
                        window.location.href = "/";
                      }}
                      className="block w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
                    >
                      Sign Out
                    </button>
                  </div>
                </div>
              )}
            </div>
          </div>
        </nav>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="mb-6">
          <h1 className="text-2xl font-semibold text-gray-900">
            Partnerships for you
          </h1>
        </div>

        {loading ? (
          <div className="flex justify-center items-center h-64">
            <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-teal-500"></div>
          </div>
        ) : error ? (
          <div className="bg-red-100 text-red-700 p-4 rounded-lg mb-6">
            {error}
            <button
              className="ml-4 underline"
              onClick={() => navigate("/partnership-search")}
            >
              返回搜索页面
            </button>
          </div>
        ) : (
          <div className="flex gap-8">
            {/* 左侧搜索和结果列表 */}
            <div className="w-1/2">
              {/* 搜索和过滤器 */}
              <div className="space-y-4 mb-6">
                <div className="flex gap-4">
                  <input
                    type="text"
                    placeholder="Search key words"
                    className="flex-1 px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-teal-500 focus:border-transparent"
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    onKeyPress={(e) => e.key === "Enter" && handleSearch()}
                  />
                  <button
                    className="px-4 py-2 bg-gray-500 text-white rounded-lg hover:bg-gray-600"
                    onClick={handleSearch}
                  >
                    AI Suggestions
                  </button>
                </div>
                <div className="flex gap-4">
                  <button className="px-4 py-2 bg-gray-100 text-gray-700 rounded-lg hover:bg-gray-200">
                    Filter
                  </button>
                  <select
                    className="px-4 py-2 bg-gray-100 text-gray-700 rounded-lg hover:bg-gray-200"
                    value={sortBy}
                    onChange={handleSort}
                  >
                    <option value="match">Sort by Match</option>
                    <option value="name">Sort by Name</option>
                    <option value="location">Sort by Location</option>
                  </select>
                </div>
              </div>

              {/* 结果列表 */}
              <div className="space-y-4">
                {results.length === 0 ? (
                  <div className="bg-white p-6 rounded-xl shadow-sm text-center">
                    <p className="text-gray-500">No matching results found</p>
                  </div>
                ) : (
                  results.map((result) => (
                    <div
                      key={result.id}
                      className="bg-white p-6 rounded-xl shadow-sm hover:shadow-md transition-shadow"
                    >
                      <div className="flex justify-between items-start">
                        <div>
                          <h3 className="text-xl font-semibold text-gray-900">
                            {result.name}
                          </h3>
                          <p className="text-gray-500">{result.location}</p>
                          <div className="flex gap-2 mt-2">
                            {result.tags.map((tag, index) => (
                              <span
                                key={index}
                                className="px-2 py-1 bg-gray-100 text-gray-600 text-sm rounded"
                              >
                                {tag}
                              </span>
                            ))}
                          </div>
                        </div>
                        <div
                          className={`text-2xl font-bold ${
                            result.matchScore >= 80
                              ? "text-green-500"
                              : result.matchScore >= 60
                              ? "text-yellow-500"
                              : "text-orange-500"
                          }`}
                        >
                          {result.matchScore}%
                        </div>
                      </div>
                    </div>
                  ))
                )}
              </div>
            </div>

            {/* 右侧网络图 */}
            <div className="w-1/2 bg-white rounded-xl shadow-sm p-4">
              <h2 className="text-lg font-semibold mb-4">Network Map</h2>
              <div className="h-[600px]">
                <ForceGraph2D
                  graphData={graphData}
                  nodeColor={(node) => node.color}
                  nodeLabel={(node) => `${node.name} (${node.score}%)`}
                  linkWidth={(link) => link.value * 3}
                  linkColor={() => "#ddd"}
                  nodeRelSize={8}
                  backgroundColor="#ffffff"
                />
              </div>
              <div className="mt-4 flex justify-end gap-4">
                <div className="flex items-center gap-2">
                  <span className="w-3 h-3 rounded-full bg-[#4CAF50]"></span>
                  <span className="text-sm text-gray-600">80-100%</span>
                </div>
                <div className="flex items-center gap-2">
                  <span className="w-3 h-3 rounded-full bg-[#FFC107]"></span>
                  <span className="text-sm text-gray-600">60%-80%</span>
                </div>
                <div className="flex items-center gap-2">
                  <span className="w-3 h-3 rounded-full bg-[#FF5722]"></span>
                  <span className="text-sm text-gray-600">20%-60%</span>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
