import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import heroImage from "../images/hero4.jpg";

const API_BASE_URL = "http://localhost:3001/api";

const PartnershipMatch = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    lookingForType: 'forprofit',
    location: '',
    partnershipGoal: ''
  });
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError('');

    if (!formData.lookingForType || !formData.partnershipGoal) {
      setError('请填写所有必填字段');
      setIsLoading(false);
      return;
    }

    try {
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('请先登录');
      }

      console.log('发送搜索请求:', formData);

      const response = await fetch(`${API_BASE_URL}/search/match`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({
          organizationName: '',
          industry: '',
          specificIndustry: '',
          state: formData.location,
          city: '',
          missionStatement: '',
          coreValues: '',
          lookingForType: formData.lookingForType,
          partnershipDesc: formData.partnershipGoal
        })
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || '请求的资源不存在');
      }

      const result = await response.json();
      console.log('搜索响应:', result);

      if (result.code === 0 && result.data && result.data.matches) {
        navigate('/results', { 
          state: { 
            matches: result.data.matches,
            totalMatches: result.data.totalMatches || 0,
            gptVerifiedCount: result.data.gptVerifiedCount || 0
          } 
        });
      } else {
        throw new Error(result.message || '未找到匹配的合作伙伴');
      }
    } catch (err) {
      console.error('搜索错误:', err);
      setError(err.message || '请求的资源不存在');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen relative overflow-hidden">
      {/* Background image and filter */}
      <div className="absolute inset-0 z-0">
        <img
          src={heroImage}
          alt="Background"
          className="w-full h-full object-cover"
          style={{
            filter: "brightness(0.65) saturate(0.8) hue-rotate(10deg)",
          }}
        />
        <div
          className="absolute inset-0"
          style={{
            background:
              "linear-gradient(to bottom right, rgba(49, 163, 159, 0.1), rgba(72, 187, 177, 0.2))",
            mixBlendMode: "multiply",
          }}
        />
      </div>

      {/* Back button */}
      <Link
        to="/home"
        className="absolute top-8 left-8 text-white hover:text-teal-200 z-10 flex items-center gap-2 transition-all duration-300 group"
      >
        <div className="bg-white/10 backdrop-blur-md p-2 rounded-full group-hover:bg-white/20 transition-all duration-300">
          <svg
            className="w-5 h-5"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              d="M10 19l-7-7m0 0l7-7m-7 7h18"
            />
          </svg>
        </div>
        <span className="text-sm font-medium">Back</span>
      </Link>

      {/* Main content */}
      <div className="relative z-10 min-h-screen flex flex-col items-center justify-center px-4">
        <div className="w-full max-w-3xl text-center mb-12">
          <h1 className="text-6xl font-bold text-white tracking-tight">
            Find Your Perfect Partner
          </h1>
          <p className="mt-4 text-xl text-white/80">
            Tell us what kind of partner you're looking for
          </p>
        </div>

        <div className="w-full max-w-3xl bg-white/10 backdrop-blur-md rounded-3xl p-10 shadow-2xl border border-white/20">
          {error && (
            <div className="mb-6 p-4 bg-red-900/30 border border-red-400 rounded-lg">
              <p className="text-red-300 text-sm">{error}</p>
            </div>
          )}

          <form onSubmit={handleSubmit} className="space-y-6">
            {/* Organization Type */}
            <div className="space-y-2">
              <label className="block text-white text-sm font-medium pl-1">
                Looking for <span className="text-red-400">*</span>
              </label>
              <select
                name="lookingForType"
                value={formData.lookingForType}
                onChange={handleInputChange}
                className="w-full px-4 py-3 bg-white/10 border border-white/30 rounded-xl text-white focus:outline-none focus:ring-2 focus:ring-teal-500 focus:border-transparent transition-all duration-300 appearance-none"
                style={{
                  backgroundImage: `url("data:image/svg+xml,%3csvg xmlns='http://www.w3.org/2000/svg' fill='none' viewBox='0 0 20 20'%3e%3cpath stroke='%23ffffff' stroke-linecap='round' stroke-linejoin='round' stroke-width='1.5' d='M6 8l4 4 4-4'/%3e%3c/svg%3e")`,
                  backgroundPosition: 'right 0.5rem center',
                  backgroundRepeat: 'no-repeat',
                  backgroundSize: '1.5em 1.5em',
                  paddingRight: '2.5rem'
                }}
              >
                <option value="forprofit" className="bg-gray-800 text-white">For-Profit Company</option>
                <option value="nonprofit" className="bg-gray-800 text-white">Non-Profit Organization</option>
              </select>
            </div>

            {/* Location */}
            <div className="space-y-2">
              <label className="block text-white text-sm font-medium pl-1">
                Preferred Location
              </label>
              <input
                type="text"
                name="location"
                value={formData.location}
                onChange={handleInputChange}
                className="w-full px-4 py-3 bg-white/10 border border-white/30 rounded-xl text-white placeholder-white/60 focus:outline-none focus:ring-2 focus:ring-teal-500 focus:border-transparent transition-all duration-300"
                placeholder="Enter city or state (optional)"
              />
            </div>

            {/* Partnership Goals */}
            <div className="space-y-2">
              <label className="block text-white text-sm font-medium pl-1">
                Partnership Goals <span className="text-red-400">*</span>
              </label>
              <textarea
                name="partnershipGoal"
                value={formData.partnershipGoal}
                onChange={handleInputChange}
                className="w-full px-4 py-3 bg-white/10 border border-white/30 rounded-xl text-white placeholder-white/60 focus:outline-none focus:ring-2 focus:ring-teal-500 focus:border-transparent transition-all duration-300 h-32 resize-none"
                placeholder="Describe what you're looking for in a partner and your goals for the partnership"
                required
              />
            </div>

            {/* Submit Button */}
            <div className="pt-4 flex flex-col items-center">
              <button
                type="submit"
                className="bg-teal-500 hover:bg-teal-400 text-white font-medium py-3 px-12 rounded-xl transition-all duration-300 transform hover:scale-[1.02] active:scale-[0.98] focus:outline-none focus:ring-2 focus:ring-teal-500 focus:ring-offset-2 focus:ring-offset-transparent disabled:opacity-70 disabled:cursor-not-allowed w-full sm:w-auto"
                disabled={isLoading}
              >
                {isLoading ? (
                  <div className="flex items-center justify-center">
                    <svg
                      className="animate-spin -ml-1 mr-2 h-4 w-4 text-white"
                      xmlns="http://www.w3.org/2000/svg"
                      fill="none"
                      viewBox="0 0 24 24"
                    >
                      <circle
                        className="opacity-25"
                        cx="12"
                        cy="12"
                        r="10"
                        stroke="currentColor"
                        strokeWidth="4"
                      ></circle>
                      <path
                        className="opacity-75"
                        fill="currentColor"
                        d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
                      ></path>
                    </svg>
                    Finding matches...
                  </div>
                ) : (
                  "Find Partners"
                )}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default PartnershipMatch;
