import { useState, useEffect } from "react";
import { useNavigate, Link, useLocation } from "react-router-dom";
import ForceGraph2D from "react-force-graph-2d";
import { useAuth } from "../context/AuthContext";
import logo from "../images/logo.svg";

export default function ResultPage() {
  const navigate = useNavigate();
  const location = useLocation();
  const { user } = useAuth();
  const [isProfileMenuOpen, setIsProfileMenuOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [sortBy, setSortBy] = useState("match");
  const [results, setResults] = useState([]);
  const [graphData, setGraphData] = useState({
    nodes: [],
    links: [],
  });
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState("");

  // 添加点击外部关闭菜单的处理函数
  useEffect(() => {
    const closeMenu = (e) => {
      if (isProfileMenuOpen && !e.target.closest(".profile-menu-container")) {
        setIsProfileMenuOpen(false);
      }
    };

    document.addEventListener("click", closeMenu);
    return () => document.removeEventListener("click", closeMenu);
  }, [isProfileMenuOpen]);

  useEffect(() => {
    // 检查是否有从 PartnershipSearch 页面传递过来的数据
    const searchState = location.state;

    if (
      searchState?.matchingResults &&
      searchState.matchingResults.length > 0
    ) {
      // 使用传递过来的匹配结果
      setResults(searchState.matchingResults);
      generateGraphData(searchState.matchingResults);
    } else {
      // 如果没有传递数据，则从 API 获取
      fetchMatchingResults();
    }
  }, [location]);

  const fetchMatchingResults = async () => {
    setIsLoading(true);
    setError("");

    try {
      const token = localStorage.getItem("token");
      if (!token) {
        throw new Error("未登录，请先登录");
      }

      const response = await fetch(
        "http://localhost:3001/api/profile/matching-results",
        {
          method: "GET",
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || "获取匹配结果失败");
      }

      setResults(data.results || []);
      generateGraphData(data.results || []);
    } catch (error) {
      console.error("获取匹配结果错误:", error);
      setError(error.message || "获取匹配结果时出错");
    } finally {
      setIsLoading(false);
    }
  };

  const generateGraphData = (matchResults) => {
    if (!matchResults || matchResults.length === 0) {
      setGraphData({ nodes: [], links: [] });
      return;
    }

    // 生成网络图数据
    const nodes = matchResults.map((result) => ({
      id: result.id,
      name: result.name || result.orgName,
      score: result.matchScore,
      color: getColorByScore(result.matchScore),
    }));

    // 添加当前用户节点
    nodes.push({
      id: "current-user",
      name: user?.orgName || "Your Organization",
      score: 100,
      color: "#4CAF50",
      size: 15, // 当前用户节点更大
    });

    const links = [];
    // 生成连接 - 所有组织都与当前用户相连
    nodes.forEach((node) => {
      if (node.id !== "current-user") {
        links.push({
          source: "current-user",
          target: node.id,
          value: node.score / 100,
        });
      }
    });

    // 生成组织之间的连接
    nodes.forEach((node, i) => {
      if (node.id === "current-user") return;

      nodes.slice(i + 1).forEach((target) => {
        if (target.id !== "current-user" && Math.random() > 0.7) {
          // 减少连接数量
          links.push({
            source: node.id,
            target: target.id,
            value: Math.random() * 0.5, // 连接强度较弱
          });
        }
      });
    });

    setGraphData({ nodes, links });
  };

  const getColorByScore = (score) => {
    if (score >= 80) return "#4CAF50";
    if (score >= 60) return "#FFC107";
    return "#FF5722";
  };

  const handleSearch = (e) => {
    e.preventDefault();
    // 实现搜索功能
    const filteredResults = results.filter(
      (result) =>
        result.name?.toLowerCase().includes(searchQuery.toLowerCase()) ||
        result.orgName?.toLowerCase().includes(searchQuery.toLowerCase()) ||
        result.location?.toLowerCase().includes(searchQuery.toLowerCase())
    );
    setResults(filteredResults);
  };

  const handleReset = () => {
    setSearchQuery("");
    // 重新获取所有结果
    if (location.state?.matchingResults) {
      setResults(location.state.matchingResults);
      generateGraphData(location.state.matchingResults);
    } else {
      fetchMatchingResults();
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* 顶部导航栏 */}
      <div
        className="App-header"
        style={{ height: "auto", minHeight: "auto", padding: "0" }}
      >
        <nav>
          <div className="logo">
            <img src={logo} alt="logo" />
            <span>Cause-connect</span>
          </div>
          <div className="nav-links">
            <a href="#">Explore</a>
            <Link to="/partnership-search">Partnership</Link>
            <a href="#">Solutions</a>
            <div className="profile-menu-container relative">
              <div
                className="flex items-center gap-1 ml-4 cursor-pointer"
                onClick={() => setIsProfileMenuOpen(!isProfileMenuOpen)}
              >
                <div className="w-8 h-8 rounded-full bg-gray-100 flex items-center justify-center">
                  <svg
                    className="w-5 h-5 text-gray-600"
                    fill="none"
                    stroke="currentColor"
                    viewBox="0 0 24 24"
                  >
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      strokeWidth="2"
                      d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z"
                    />
                  </svg>
                </div>
                <span className="text-sm text-gray-700 font-medium">
                  {user?.orgName}
                </span>
                <svg
                  className={`w-4 h-4 text-gray-500 transition-transform duration-200 ${
                    isProfileMenuOpen ? "transform rotate-180" : ""
                  }`}
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth="2"
                    d="M19 9l-7 7-7-7"
                  />
                </svg>
              </div>

              {/* 下拉菜单 */}
              {isProfileMenuOpen && (
                <div className="absolute right-0 top-full mt-1 w-48 rounded-md shadow-lg bg-white ring-1 ring-black ring-opacity-5 z-50">
                  <div className="py-1">
                    <a
                      href="#"
                      className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
                    >
                      Profile Settings
                    </a>
                    <button
                      onClick={() => {
                        localStorage.removeItem("token");
                        window.location.href = "/";
                      }}
                      className="block w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
                    >
                      Sign Out
                    </button>
                  </div>
                </div>
              )}
            </div>
          </div>
        </nav>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="mb-6">
          <h1 className="text-2xl font-semibold text-gray-900">
            Partnerships for you
          </h1>
          {location.state?.searchParams && (
            <div className="mt-2 text-sm text-gray-500">
              <span>Search criteria: </span>
              {location.state.searchParams.location && (
                <span className="mr-2">
                  Location: {location.state.searchParams.location}
                </span>
              )}
              {location.state.searchParams.organizationType && (
                <span className="mr-2">
                  Organization Type:{" "}
                  {location.state.searchParams.organizationType}
                </span>
              )}
            </div>
          )}
        </div>

        <div className="flex gap-8">
          {/* 左侧搜索和结果列表 */}
          <div className="w-1/2">
            {/* 搜索和过滤器 */}
            <div className="space-y-4 mb-6">
              <form onSubmit={handleSearch} className="flex gap-4">
                <input
                  type="text"
                  placeholder="Search key words"
                  className="flex-1 px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-teal-500 focus:border-transparent"
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                />
                <button
                  type="submit"
                  className="px-4 py-2 bg-teal-500 text-white rounded-lg hover:bg-teal-600"
                >
                  Search
                </button>
                <button
                  type="button"
                  onClick={handleReset}
                  className="px-4 py-2 bg-gray-500 text-white rounded-lg hover:bg-gray-600"
                >
                  Reset
                </button>
              </form>
              <div className="flex gap-4">
                <button className="px-4 py-2 bg-gray-100 text-gray-700 rounded-lg hover:bg-gray-200">
                  Filter
                </button>
                <select
                  className="px-4 py-2 bg-gray-100 text-gray-700 rounded-lg hover:bg-gray-200"
                  value={sortBy}
                  onChange={(e) => {
                    setSortBy(e.target.value);
                    // 实现排序逻辑
                    const sortedResults = [...results].sort((a, b) => {
                      if (e.target.value === "match") {
                        return b.matchScore - a.matchScore;
                      } else if (e.target.value === "name") {
                        return (a.name || a.orgName || "").localeCompare(
                          b.name || b.orgName || ""
                        );
                      } else if (e.target.value === "location") {
                        return (a.location || "").localeCompare(
                          b.location || ""
                        );
                      }
                      return 0;
                    });
                    setResults(sortedResults);
                  }}
                >
                  <option value="match">Sort by Match</option>
                  <option value="name">Sort by Name</option>
                  <option value="location">Sort by Location</option>
                </select>
              </div>
            </div>

            {/* 错误提示 */}
            {error && (
              <div className="mb-4 p-3 bg-red-100 text-red-700 rounded-lg">
                {error}
              </div>
            )}

            {/* 加载状态 */}
            {isLoading ? (
              <div className="flex justify-center items-center h-64">
                <svg
                  className="animate-spin h-10 w-10 text-teal-500"
                  xmlns="http://www.w3.org/2000/svg"
                  fill="none"
                  viewBox="0 0 24 24"
                >
                  <circle
                    className="opacity-25"
                    cx="12"
                    cy="12"
                    r="10"
                    stroke="currentColor"
                    strokeWidth="4"
                  ></circle>
                  <path
                    className="opacity-75"
                    fill="currentColor"
                    d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
                  ></path>
                </svg>
              </div>
            ) : (
              <>
                {/* 结果列表 */}
                {results.length === 0 ? (
                  <div className="text-center py-10">
                    <p className="text-gray-500">
                      No matching partners found. Try adjusting your search
                      criteria.
                    </p>
                  </div>
                ) : (
                  <div className="space-y-4">
                    {results.map((result) => (
                      <div
                        key={result.id}
                        className="bg-white p-6 rounded-xl shadow-sm hover:shadow-md transition-shadow"
                      >
                        <div className="flex justify-between items-start">
                          <div>
                            <h3 className="text-xl font-semibold text-gray-900">
                              {result.name || result.orgName}
                            </h3>
                            <p className="text-gray-500">{result.location}</p>
                            <div className="flex gap-2 mt-2">
                              {result.tags &&
                                result.tags.map((tag, index) => (
                                  <span
                                    key={index}
                                    className="px-2 py-1 bg-gray-100 text-gray-600 text-sm rounded"
                                  >
                                    {tag}
                                  </span>
                                ))}
                              {result.industry && (
                                <span className="px-2 py-1 bg-gray-100 text-gray-600 text-sm rounded">
                                  {result.industry}
                                </span>
                              )}
                            </div>
                          </div>
                          <div className="text-2xl font-bold text-teal-500">
                            {result.matchScore}%
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </>
            )}
          </div>

          {/* 右侧网络图 */}
          <div className="w-1/2 bg-white rounded-xl shadow-sm p-4">
            <h2 className="text-lg font-semibold mb-4">Network Map</h2>
            <div className="h-[600px]">
              {graphData.nodes.length > 0 ? (
                <ForceGraph2D
                  graphData={graphData}
                  nodeColor={(node) => node.color}
                  nodeLabel={(node) => `${node.name} (${node.score}%)`}
                  linkWidth={(link) => link.value * 2}
                  linkColor={() => "#ddd"}
                  nodeRelSize={(node) => (node.id === "current-user" ? 10 : 6)}
                  backgroundColor="#ffffff"
                />
              ) : (
                <div className="flex justify-center items-center h-full">
                  <p className="text-gray-500">
                    No data available for network visualization
                  </p>
                </div>
              )}
            </div>
            <div className="mt-4 flex justify-end gap-4">
              <div className="flex items-center gap-2">
                <span className="w-3 h-3 rounded-full bg-[#4CAF50]"></span>
                <span className="text-sm text-gray-600">80-100%</span>
              </div>
              <div className="flex items-center gap-2">
                <span className="w-3 h-3 rounded-full bg-[#FFC107]"></span>
                <span className="text-sm text-gray-600">60%-80%</span>
              </div>
              <div className="flex items-center gap-2">
                <span className="w-3 h-3 rounded-full bg-[#FF5722]"></span>
                <span className="text-sm text-gray-600">20%-60%</span>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
