          <button
            onClick={() => handleTypeSelect("for-profit")}
            className="w-full flex items-center justify-center px-8 py-6 border-2 border-gray-300 rounded-xl hover:border-gray-400 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gray-500"
          >
            <div className="text-left">
              <span className="text-2xl mb-2">üíº</span>
              <h3 className="text-lg font-medium text-gray-900">
                We're For-Profits
              </h3>
              <p className="text-sm text-gray-500">
                Companies and businesses looking to make an impact
              </p>
            </div>
          </button>
        </div>
      </div>
    </div>
  );
}
