import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";

export default function ProfileTypePage() {
  const navigate = useNavigate();
  const { user } = useAuth();
  const [selectedType, setSelectedType] = useState("");

  const nonprofitIndustries = [
    "Government & Nonprofit",
    "Education & Research",
    "Healthcare & Pharmaceuticals",
    "Energy & Environment",
    "Legal & Consulting",
  ];

  const forProfitIndustries = [
    "Technology & Software",
    "Finance & Banking",
    "Manufacturing & Engineering",
    "Retail & Consumer Goods",
    "Media & Entertainment",
    "Construction & Real Estate",
    "Transportation & Logistics",
  ];

  const [industries, setIndustries] = useState([]);

  useEffect(() => {
    console.log("ProfileTypePage: 页面加载");
    console.log("当前用户状态:", user);

    if (!user) {
      console.log("未检测到用户，准备重定向到登录页面");
      navigate("/signin");
    } else {
      console.log("检测到用户，允许访问页面");
    }

    if (selectedType === "nonprofit") {
      setIndustries(nonprofitIndustries);
    } else if (selectedType === "for-profit") {
      setIndustries(forProfitIndustries);
    }
  }, [user, navigate, selectedType]);

  const handleTypeSelect = (type) => {
    localStorage.setItem("orgType", type);
    navigate("/profile-setup/details");
  };

  return (
    <div className="min-h-screen flex items-center justify-center px-4 sm:px-6 lg:px-8">
      <div className="max-w-md w-full space-y-8">
        <div>
          <h2 className="mt-6 text-center text-3xl font-bold text-gray-900">
            Tell us about your organization
          </h2>
          <p className="mt-2 text-center text-sm text-gray-600">
            Select your organization type to help us provide better partnership
            matches
          </p>
        </div>

        <div className="mt-8 space-y-4">
          <button
            onClick={() => handleTypeSelect("nonprofit")}
            className="w-full flex items-center justify-center px-8 py-6 border-2 border-gray-300 rounded-xl hover:border-gray-400 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gray-500"
          >
            <div className="text-left">
              <span className="text-2xl mb-2">🌍</span>
              <h3 className="text-lg font-medium text-gray-900">
                We're Nonprofits
              </h3>
              <p className="text-sm text-gray-500">
                NGOs, charities, and social organizations
              </p>
            </div>
          </button>

          <button
            onClick={() => handleTypeSelect("for-profit")}
            className="w-full flex items-center justify-center px-8 py-6 border-2 border-gray-300 rounded-xl hover:border-gray-400 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gray-500"
          >
            <div className="text-left">
              <span className="text-2xl mb-2">💼</span>
              <h3 className="text-lg font-medium text-gray-900">
                We're For-Profits
              </h3>
              <p className="text-sm text-gray-500">
                Companies and businesses looking to make an impact
              </p>
            </div>
          </button>

          {selectedType && (
            <div className="mt-4">
              <label className="block text-gray-700 text-sm font-medium mb-2">
                Select Industry
              </label>
              <select className="w-full px-4 py-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-gray-500">
                {industries.map((industry) => (
                  <option key={industry} value={industry}>
                    {industry}
                  </option>
                ))}
              </select>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
