import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import heroImage from "../images/hero4.jpg";
import { useAuth } from "../context/AuthContext";

// API 基础 URL
const API_BASE_URL = "http://localhost:3001/api";

const PartnershipSearch = () => {
  const navigate = useNavigate();
  const { user } = useAuth();
  const [formData, setFormData] = useState({
    organizationName: "",
    industry: "",
    specificIndustry: "",
    state: "",
    city: "",
    missionStatement: "",
    coreValues: "",
  });
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState("");

  // 加载组织信息
  useEffect(() => {
    const fetchOrganizationInfo = async () => {
      try {
        const token = localStorage.getItem("token");
        if (!token) {
          setError("请先登录");
          return;
        }

        // 获取组织基本信息
        const response = await fetch(`${API_BASE_URL}/profile`, {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });

        // 获取伙伴搜索信息
        const searchResponse = await fetch(`${API_BASE_URL}/partner-search/search`, {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });

        if (response.ok && searchResponse.ok) {
          const orgData = await response.json();
          const searchData = await searchResponse.json();

          if (orgData.code === 0 && orgData.data) {
            const profileData = orgData.data;
            const searchInfo = searchData.code === 0 ? searchData.data : {};

            // 检查是否已填写完整信息
            if (
              profileData.name &&
              profileData.industry &&
              profileData.location?.city &&
              profileData.location?.state &&
              profileData.missionStatement &&
              searchInfo.partnerSearch
            ) {
              // 信息完整，直接跳转到主页
              navigate("/home");
              return;
            }

            // 信息不完整，填充现有数据
            setFormData({
              organizationName: profileData.name || "",
              industry: profileData.industry || "",
              specificIndustry: profileData.specificIndustry || "",
              state: profileData.location?.state || "",
              city: profileData.location?.city || "",
              missionStatement: profileData.missionStatement || "",
              coreValues: profileData.coreValues || "",
            });
            setError("");
          }
        } else {
          console.error("获取组织信息失败");
        }
      } catch (err) {
        console.error("获取组织信息出错:", err);
      }
    };

    fetchOrganizationInfo();
  }, [navigate]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      const token = localStorage.getItem("token");
      if (!token) {
        throw new Error("请先登录");
      }

      // 更新组织基本信息
      const orgResponse = await fetch(`${API_BASE_URL}/profile`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`
        },
        body: JSON.stringify({
          name: formData.organizationName,
          industry: formData.industry,
          specificIndustry: formData.specificIndustry,
          location: {
            state: formData.state,
            city: formData.city
          },
          missionStatement: formData.missionStatement,
          coreValues: formData.coreValues
        })
      });

      // 更新伙伴搜索信息
      const searchResponse = await fetch(`${API_BASE_URL}/partner-search/search`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`
        },
        body: JSON.stringify({
          location: formData.city,
          organizationType: "nonprofit",
          partnershipGoal: formData.missionStatement
        })
      });

      if (!orgResponse.ok || !searchResponse.ok) {
        throw new Error("保存失败");
      }

      // 保存成功后跳转到主页
      navigate("/home");
    } catch (err) {
      setError(err.message);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen relative overflow-hidden">
      {/* 背景图片和滤镜效果 */}
      <div className="absolute inset-0 z-0">
        <img
          src={heroImage}
          alt="Background"
          className="w-full h-full object-cover"
          style={{
            filter: "brightness(0.65) saturate(0.8) hue-rotate(10deg)",
          }}
        />
        <div
          className="absolute inset-0"
          style={{
            background:
              "linear-gradient(to bottom right, rgba(49, 163, 159, 0.1), rgba(72, 187, 177, 0.2))",
            mixBlendMode: "multiply",
          }}
        />
      </div>

      {/* 返回按钮 */}
      <Link
        to="/home"
        className="absolute top-8 left-8 text-white hover:text-teal-200 z-10 flex items-center gap-2 transition-all duration-300 group"
      >
        <div className="bg-white/10 backdrop-blur-md p-2 rounded-full group-hover:bg-white/20 transition-all duration-300">
          <svg
            className="w-5 h-5"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              d="M10 19l-7-7m0 0l7-7m-7 7h18"
            />
          </svg>
        </div>
        <span className="text-sm font-medium">Back</span>
      </Link>

      {/* 主要内容 */}
      <div className="relative z-10 min-h-screen flex flex-col items-center justify-center px-4">
        <div className="w-full max-w-3xl text-center mb-12">
          <h1 className="text-6xl font-bold text-white tracking-tight">
            Tell us about your organization
          </h1>
        </div>

        <div className="w-full max-w-3xl bg-white/10 backdrop-blur-md rounded-3xl p-10 shadow-2xl border border-white/20">
          {error && (
            <div className="mb-6 p-4 bg-red-900/30 border border-red-400 rounded-lg">
              <p className="text-red-300 text-sm">{error}</p>
            </div>
          )}

          <form onSubmit={handleSubmit} className="space-y-6">
            {/* Organization Name */}
            <div className="space-y-2">
              <label className="block text-white text-sm font-medium pl-1">
                Organization Name
              </label>
              <input
                type="text"
                name="organizationName"
                value={formData.organizationName}
                onChange={handleInputChange}
                className="w-full px-4 py-3 bg-white/10 border border-white/30 rounded-xl text-white placeholder-white/60 focus:outline-none focus:ring-2 focus:ring-teal-500 focus:border-transparent transition-all duration-300"
                placeholder="Enter organization name"
              />
            </div>

            {/* Industry */}
            <div className="space-y-2">
              <label className="block text-white text-sm font-medium pl-1">
                Industry
              </label>
              <input
                type="text"
                name="industry"
                value={formData.industry}
                onChange={handleInputChange}
                className="w-full px-4 py-3 bg-white/10 border border-white/30 rounded-xl text-white placeholder-white/60 focus:outline-none focus:ring-2 focus:ring-teal-500 focus:border-transparent transition-all duration-300"
                placeholder="Technology & Software"
              />
            </div>

            {/* Specific Industry */}
            <div className="space-y-2">
              <label className="block text-white text-sm font-medium pl-1">
                Select Specific Industry
              </label>
              <input
                type="text"
                name="specificIndustry"
                value={formData.specificIndustry}
                onChange={handleInputChange}
                className="w-full px-4 py-3 bg-white/10 border border-white/30 rounded-xl text-white placeholder-white/60 focus:outline-none focus:ring-2 focus:ring-teal-500 focus:border-transparent transition-all duration-300"
                placeholder="Select Specific Industry"
              />
            </div>

            {/* Location */}
            <div className="grid grid-cols-2 gap-6">
              <div className="space-y-2">
                <label className="block text-white text-sm font-medium pl-1">
                  State
                </label>
                <input
                  type="text"
                  name="state"
                  value={formData.state}
                  onChange={handleInputChange}
                  className="w-full px-4 py-3 bg-white/10 border border-white/30 rounded-xl text-white placeholder-white/60 focus:outline-none focus:ring-2 focus:ring-teal-500 focus:border-transparent transition-all duration-300"
                  placeholder="Select State"
                />
              </div>
              <div className="space-y-2">
                <label className="block text-white text-sm font-medium pl-1">
                  City
                </label>
                <input
                  type="text"
                  name="city"
                  value={formData.city}
                  onChange={handleInputChange}
                  className="w-full px-4 py-3 bg-white/10 border border-white/30 rounded-xl text-white placeholder-white/60 focus:outline-none focus:ring-2 focus:ring-teal-500 focus:border-transparent transition-all duration-300"
                  placeholder="Select City"
                />
              </div>
            </div>

            {/* Mission Statement */}
            <div className="space-y-2">
              <label className="block text-white text-sm font-medium pl-1">
                Mission Statement
              </label>
              <textarea
                name="missionStatement"
                value={formData.missionStatement}
                onChange={handleInputChange}
                className="w-full px-4 py-3 bg-white/10 border border-white/30 rounded-xl text-white placeholder-white/60 focus:outline-none focus:ring-2 focus:ring-teal-500 focus:border-transparent transition-all duration-300 h-24 resize-none"
                placeholder="Enter mission statement"
              />
            </div>

            {/* Core Values */}
            <div className="space-y-2">
              <label className="block text-white text-sm font-medium pl-1">
                Core Values
              </label>
              <textarea
                name="coreValues"
                value={formData.coreValues}
                onChange={handleInputChange}
                className="w-full px-4 py-3 bg-white/10 border border-white/30 rounded-xl text-white placeholder-white/60 focus:outline-none focus:ring-2 focus:ring-teal-500 focus:border-transparent transition-all duration-300 h-24 resize-none"
                placeholder="Enter core values"
              />
            </div>

            {/* Submit Button */}
            <div className="pt-4 flex flex-col items-center">
              <button
                type="submit"
                className="bg-teal-500 hover:bg-teal-400 text-white font-medium py-3 px-12 rounded-xl transition-all duration-300 transform hover:scale-[1.02] active:scale-[0.98] focus:outline-none focus:ring-2 focus:ring-teal-500 focus:ring-offset-2 focus:ring-offset-transparent disabled:opacity-70 disabled:cursor-not-allowed"
                disabled={isLoading}
              >
                {isLoading ? (
                  <div className="flex items-center">
                    <svg
                      className="animate-spin -ml-1 mr-2 h-4 w-4 text-white"
                      xmlns="http://www.w3.org/2000/svg"
                      fill="none"
                      viewBox="0 0 24 24"
                    >
                      <circle
                        className="opacity-25"
                        cx="12"
                        cy="12"
                        r="10"
                        stroke="currentColor"
                        strokeWidth="4"
                      ></circle>
                      <path
                        className="opacity-75"
                        fill="currentColor"
                        d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
                      ></path>
                    </svg>
                    Processing...
                  </div>
                ) : (
                  "Save Information"
                )}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default PartnershipSearch;
