import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import heroBg from "../images/hero-bg.jpg";

const PartnershipSearch = () => {
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState("location");
  const [formData, setFormData] = useState({
    state: "",
    city: "",
    orgType: "",
    goal: "",
  });

  const US_STATES = ["California", "New York", "Texas"]; // 添加更多州
  const CITIES = {
    California: ["Los Angeles", "San Francisco", "San Diego"],
    "New York": ["New York City", "Buffalo", "Albany"],
    Texas: ["Houston", "Austin", "Dallas"],
  };

  return (
    <div className="min-h-screen relative">
      {/* 半透明背景图 */}
      <div className="absolute inset-0">
        <img
          src={heroBg}
          alt="Background"
          className="w-full h-full object-cover"
          style={{ filter: "brightness(0.7) saturate(0.7)" }}
        />
        <div className="absolute inset-0 bg-cyan-900/30 backdrop-blur-sm"></div>
      </div>

      {/* 返回按钮 */}
      <button
        onClick={() => navigate(-1)}
        className="absolute top-8 left-8 text-white hover:text-cyan-200 transition-colors z-10"
      >
        <svg
          className="w-8 h-8"
          fill="none"
          stroke="currentColor"
          viewBox="0 0 24 24"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth="2"
            d="M10 19l-7-7m0 0l7-7m-7 7h18"
          />
        </svg>
      </button>

      {/* 主要内容 */}
      <div className="relative z-10 pt-20 px-4 sm:px-6 lg:px-8">
        <div className="max-w-5xl mx-auto">
          <h1 className="text-5xl font-bold text-white text-center mb-8">
            Let's Find Your Perfect Partner
          </h1>

          {/* 标签栏 */}
          <div className="flex justify-center gap-4 mb-8">
            {["location", "type", "goal"].map((tab) => (
              <button
                key={tab}
                onClick={() => setActiveTab(tab)}
                className={`px-6 py-3 rounded-full text-sm font-medium transition-colors
                  ${
                    activeTab === tab
                      ? "bg-cyan-500 text-white shadow-lg"
                      : "bg-white/90 text-gray-700 hover:bg-white"
                  }`}
              >
                {tab.charAt(0).toUpperCase() + tab.slice(1)}
              </button>
            ))}
          </div>

          {/* 搜索区域新布局 */}
          <div className="flex gap-6">
            {/* 左侧输入区域 */}
            <div className="flex-grow bg-white/95 rounded-xl p-6 shadow-lg">
              {activeTab === "location" && (
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      State
                    </label>
                    <select
                      value={formData.state}
                      onChange={(e) =>
                        setFormData({ ...formData, state: e.target.value })
                      }
                      className="w-full rounded-lg border-gray-300 shadow-sm focus:border-cyan-500 focus:ring-cyan-500"
                    >
                      <option value="">Select State</option>
                      {US_STATES.map((state) => (
                        <option key={state} value={state}>
                          {state}
                        </option>
                      ))}
                    </select>
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      City
                    </label>
                    <select
                      value={formData.city}
                      onChange={(e) =>
                        setFormData({ ...formData, city: e.target.value })
                      }
                      className="w-full rounded-lg border-gray-300 shadow-sm focus:border-cyan-500 focus:ring-cyan-500"
                      disabled={!formData.state}
                    >
                      <option value="">Select City</option>
                      {formData.state &&
                        CITIES[formData.state].map((city) => (
                          <option key={city} value={city}>
                            {city}
                          </option>
                        ))}
                    </select>
                  </div>
                </div>
              )}

              {activeTab === "type" && (
                <div className="grid grid-cols-2 gap-6">
                  <button
                    onClick={() =>
                      setFormData({ ...formData, orgType: "nonprofit" })
                    }
                    className={`p-6 rounded-xl border-2 transition-all
                      ${
                        formData.orgType === "nonprofit"
                          ? "border-cyan-500 bg-cyan-50"
                          : "border-gray-200 hover:border-cyan-200"
                      }`}
                  >
                    <span className="text-2xl mb-2">🌍</span>
                    <h3 className="text-lg font-medium text-gray-900">
                      Nonprofit
                    </h3>
                    <p className="text-sm text-gray-500">
                      NGOs, charities, and social organizations
                    </p>
                  </button>
                  <button
                    onClick={() =>
                      setFormData({ ...formData, orgType: "for-profit" })
                    }
                    className={`p-6 rounded-xl border-2 transition-all
                      ${
                        formData.orgType === "for-profit"
                          ? "border-cyan-500 bg-cyan-50"
                          : "border-gray-200 hover:border-cyan-200"
                      }`}
                  >
                    <span className="text-2xl mb-2">💼</span>
                    <h3 className="text-lg font-medium text-gray-900">
                      For-Profit
                    </h3>
                    <p className="text-sm text-gray-500">
                      Companies and businesses looking to make an impact
                    </p>
                  </button>
                </div>
              )}

              {activeTab === "goal" && (
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    What Kind of Organization Are You Looking For?
                  </label>
                  <textarea
                    value={formData.goal}
                    onChange={(e) =>
                      setFormData({ ...formData, goal: e.target.value })
                    }
                    rows={4}
                    className="w-full rounded-lg border-gray-300 shadow-sm focus:border-cyan-500 focus:ring-cyan-500"
                    placeholder="Describe your ideal partnership and what you hope to achieve..."
                  />
                </div>
              )}
            </div>

            {/* 右侧搜索按钮 - 固定位置 */}
            <div className="w-24 flex items-center">
              <button
                className="w-full h-12 bg-cyan-500 hover:bg-cyan-600 text-white rounded-full 
                  font-medium transition-colors shadow-lg flex items-center justify-center
                  hover:shadow-xl active:transform active:scale-95"
              >
                <svg
                  className="w-5 h-5"
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth="2"
                    d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"
                  />
                </svg>
              </button>
            </div>
          </div>

          {/* 当前选择的显示 */}
          <div className="mt-6 flex gap-3 justify-center">
            {formData.state && (
              <span className="px-4 py-2 bg-white/80 rounded-full text-sm text-gray-700">
                📍{" "}
                {formData.city
                  ? `${formData.city}, ${formData.state}`
                  : formData.state}
              </span>
            )}
            {formData.orgType && (
              <span className="px-4 py-2 bg-white/80 rounded-full text-sm text-gray-700">
                {formData.orgType === "nonprofit"
                  ? "🌍 Nonprofit"
                  : "💼 For-Profit"}
              </span>
            )}
            {formData.goal && (
              <span className="px-4 py-2 bg-white/80 rounded-full text-sm text-gray-700 truncate max-w-xs">
                🎯 {formData.goal}
              </span>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default PartnershipSearch;
