import { useState, useEffect } from "react";
import { useNavigate, useLocation, Link } from "react-router-dom";
import ForceGraph2D from "react-force-graph-2d";
import { useAuth } from "../context/AuthContext";
import logo from "../images/logo.svg";

// API åŸºç¡€ URL
const API_BASE_URL = "http://localhost:3001/api";

export default function ResultPage() {
  const navigate = useNavigate();
  const location = useLocation();
  const { user } = useAuth();
  const [isProfileMenuOpen, setIsProfileMenuOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [sortBy, setSortBy] = useState("match");
  const [results, setResults] = useState([]);
  const [graphData, setGraphData] = useState({ nodes: [], links: [] });
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState("");
  const [searchParams, setSearchParams] = useState(
    location.state?.searchParams || {}
  );

  // æ·»åŠ ç‚¹å‡»å¤–éƒ¨å…³é—­èœå•çš„å¤„ç†å‡½æ•°
  useEffect(() => {
    const closeMenu = (e) => {
      if (isProfileMenuOpen && !e.target.closest(".profile-menu-container")) {
        setIsProfileMenuOpen(false);
      }
    };

    document.addEventListener("click", closeMenu);
    return () => document.removeEventListener("click", closeMenu);
  }, [isProfileMenuOpen]);

  // æ£€æŸ¥ç”¨æˆ·ä¿¡æ¯å¹¶è·å–åŒ¹é…ç»“æœ
  useEffect(() => {
    // å¦‚æœä» location ä¼ é€’äº†é”™è¯¯ä¿¡æ¯ï¼Œæ˜¾ç¤ºé”™è¯¯
    if (location.state?.error) {
      setError(location.state.error);
    }

    // ä¿å­˜æœç´¢å‚æ•°
    if (location.state?.searchParams) {
      setSearchParams(location.state.searchParams);
    }

    // æ£€æŸ¥ç”¨æˆ·ä¿¡æ¯
    if (!user) {
      console.error("ç”¨æˆ·æœªç™»å½•");
      setError("æ— æ³•è·å–ç”¨æˆ·ä¿¡æ¯ï¼Œè¯·é‡æ–°ç™»å½•");
      return;
    }

    console.log("å½“å‰ç”¨æˆ·ä¿¡æ¯:", user);

    // å¦‚æœä» location ä¼ é€’äº†åŒ¹é…ç»“æœï¼Œä½¿ç”¨å®ƒä»¬
    if (
      location.state?.matchingResults &&
      location.state.matchingResults.length > 0
    ) {
      console.log(
        "ä½¿ç”¨ä» location ä¼ é€’çš„åŒ¹é…ç»“æœ:",
        location.state.matchingResults
      );
      setResults(location.state.matchingResults);
      generateGraphData(location.state.matchingResults);
    } else {
      // å¦åˆ™ä» API è·å–åŒ¹é…ç»“æœ
      fetchMatchingResults();
    }
  }, [location, user]);

  const fetchMatchingResults = async () => {
    setIsLoading(true);
    setError("");

    try {
      const token = localStorage.getItem("token");
      if (!token) {
        throw new Error("æœªç™»å½•ï¼Œè¯·å…ˆç™»å½•");
      }

      // ç¡®ä¿ç”¨æˆ·ä¿¡æ¯å­˜åœ¨
      if (!user) {
        console.error("ç”¨æˆ·ä¿¡æ¯ä¸å­˜åœ¨");
        throw new Error("æ— æ³•è·å–ç”¨æˆ·ä¿¡æ¯ï¼Œè¯·é‡æ–°ç™»å½•");
      }

      // è·å–ç”¨æˆ·ID - å°è¯•å¤šç§å¯èƒ½çš„å±æ€§å
      const userId = user._id || user.id || user.userId || user.user_id;

      if (!userId) {
        console.error("æ— æ³•ä»ç”¨æˆ·å¯¹è±¡ä¸­è·å–ID:", user);
        // å°è¯•ä» token ä¸­è§£æç”¨æˆ· ID
        try {
          const tokenPayload = JSON.parse(atob(token.split(".")[1]));
          console.log("Token payload:", tokenPayload);
          const tokenUserId =
            tokenPayload._id ||
            tokenPayload.id ||
            tokenPayload.userId ||
            tokenPayload.user_id ||
            tokenPayload.sub;

          if (tokenUserId) {
            console.log("ä» token ä¸­è·å–åˆ°ç”¨æˆ· ID:", tokenUserId);
            // ä½¿ç”¨ä» token ä¸­è·å–çš„ç”¨æˆ· ID
            fetchDataWithUserId(token, tokenUserId);
            return;
          } else {
            throw new Error("Token ä¸­ä¸åŒ…å«ç”¨æˆ· ID");
          }
        } catch (tokenError) {
          console.error("è§£æ token å¤±è´¥:", tokenError);
          throw new Error("æ— æ³•è·å–ç”¨æˆ· IDï¼Œè¯·é‡æ–°ç™»å½•");
        }
      }

      console.log("å½“å‰ç”¨æˆ·ID:", userId);

      // ä½¿ç”¨ç”¨æˆ· ID è·å–æ•°æ®
      fetchDataWithUserId(token, userId);
    } catch (error) {
      console.error("è·å–åŒ¹é…ç»“æœé”™è¯¯:", error);
      setError(error.message || "è·å–åŒ¹é…ç»“æœæ—¶å‡ºé”™");

      // å³ä½¿å‡ºé”™ï¼Œä¹Ÿæ˜¾ç¤ºæ¨¡æ‹Ÿæ•°æ®
      const mockResults = generateMockResults(searchParams);
      console.log("ä½¿ç”¨æ¨¡æ‹Ÿæ•°æ®:", mockResults);
      setResults(mockResults);
      generateGraphData(mockResults);
    } finally {
      setIsLoading(false);
    }
  };

  // ä½¿ç”¨ç”¨æˆ· ID è·å–æ•°æ®çš„å‡½æ•°
  const fetchDataWithUserId = async (token, userId) => {
    try {
      console.log(`å°è¯•ä½¿ç”¨ç”¨æˆ· ID ${userId} è·å–åŒ¹é…ç»“æœ...`);

      // é¦–å…ˆå°è¯• profile/matching-results ç«¯ç‚¹
      try {
        const response = await fetch(
          `${API_BASE_URL}/profile/matching-results`,
          {
            method: "GET",
