import { useState, useEffect } from "react";
import { useNavigate, useLocation, Link } from "react-router-dom";
import ForceGraph2D from "react-force-graph-2d";
import { useAuth } from "../context/AuthContext";
import logo from "../images/logo.svg";

// API åŸºç¡€ URL
const API_BASE_URL = "http://localhost:3001/api";

export default function ResultPage() {
  const navigate = useNavigate();
  const location = useLocation();
  const { user } = useAuth();
  const [isProfileMenuOpen, setIsProfileMenuOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [sortBy, setSortBy] = useState("match");
  const [results, setResults] = useState([]);
  const [graphData, setGraphData] = useState({ nodes: [], links: [] });
  const [isLoading, setIsLoading] = useState(true); // é»˜è®¤ä¸ºåŠ è½½çŠ¶æ€
  const [error, setError] = useState("");
  const [searchParams, setSearchParams] = useState(
    location.state?.searchParams || {}
  );
  const [dataSource, setDataSource] = useState(""); // ç”¨äºè·Ÿè¸ªæ•°æ®æ¥æº

  // æ·»åŠ ç‚¹å‡»å¤–éƒ¨å…³é—­èœå•çš„å¤„ç†å‡½æ•°
  useEffect(() => {
    const closeMenu = (e) => {
      if (isProfileMenuOpen && !e.target.closest(".profile-menu-container")) {
        setIsProfileMenuOpen(false);
      }
    };

    document.addEventListener("click", closeMenu);
    return () => document.removeEventListener("click", closeMenu);
  }, [isProfileMenuOpen]);

  // é¡µé¢åŠ è½½æ—¶é‡ç½®çŠ¶æ€å¹¶è·å–æ•°æ®
  useEffect(() => {
    // é‡ç½®çŠ¶æ€
    setResults([]);
    setGraphData({ nodes: [], links: [] });
    setError("");
    setIsLoading(true);
    setDataSource("");

    console.log("ResultPage åˆå§‹åŒ–...");
    console.log("location.state:", location.state);

    // å¦‚æœä» location ä¼ é€’äº†é”™è¯¯ä¿¡æ¯ï¼Œæ˜¾ç¤ºé”™è¯¯
    if (location.state?.error) {
      setError(location.state.error);
    }

    // ä¿å­˜æœç´¢å‚æ•°
    if (location.state?.searchParams) {
      setSearchParams(location.state.searchParams);
    }

    // é‡æ–°è·å–åŒ¹é…ç»“æœï¼Œä¸ä½¿ç”¨ä¹‹å‰ä¼ é€’çš„æ•°æ®
    fetchMatchingResults();
  }, []);

  const fetchMatchingResults = async () => {
    setIsLoading(true);
    setError("");
    setResults([]);
    setGraphData({ nodes: [], links: [] });
    setDataSource("");

    try {
      const token = localStorage.getItem("token");
      if (!token) {
        throw new Error("æœªç™»å½•ï¼Œè¯·å…ˆç™»å½•");
      }

      if (!user || (!user._id && !user.id)) {
        console.error("ç”¨æˆ·ä¿¡æ¯ä¸å®Œæ•´:", user);
        throw new Error("æ— æ³•è·å–ç”¨æˆ·ä¿¡æ¯ï¼Œè¯·é‡æ–°ç™»å½•");
      }

      const userId = user._id || user.id;
      console.log("å½“å‰ç”¨æˆ·ID:", userId);

      console.log("å°è¯•ä» API è·å–åŒ¹é…ç»“æœ...");
      console.log("searchParams:", searchParams);

      // é¦–å…ˆå°è¯• profile/matching-results ç«¯ç‚¹
      try {
        const response = await fetch(
          `${API_BASE_URL}/profile/matching-results`,
          {
            method: "GET",
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );

        console.log("matching-results å“åº”çŠ¶æ€:", response.status);

        if (!response.ok) {
          console.warn(
            "ä» matching-results ç«¯ç‚¹è·å–å¤±è´¥ï¼ŒçŠ¶æ€ç :",
            response.status
          );

          // å¦‚æœç¬¬ä¸€ä¸ªç«¯ç‚¹å¤±è´¥ï¼Œå°è¯• match_partners ç«¯ç‚¹
          const alternativeResponse = await fetch(
            `${API_BASE_URL}/match_partners?user_id=${userId}`,
            {
              headers: {
                Authorization: `Bearer ${token}`,
              },
            }
          );

          console.log("match_partners å“åº”çŠ¶æ€:", alternativeResponse.status);

          if (!alternativeResponse.ok) {
            console.warn(
              "ä» match_partners ç«¯ç‚¹è·å–å¤±è´¥ï¼ŒçŠ¶æ€ç :",
              alternativeResponse.status
            );
            throw new Error("æ— æ³•è·å–åŒ¹é…ç»“æœï¼Œè¯·ç¨åå†è¯•");
          }

          const alternativeData = await alternativeResponse.json();
          console.log("ä» match_partners è·å–çš„åŒ¹é…ç»“æœ:", alternativeData);

          if (alternativeData.results && alternativeData.results.length > 0) {
            setDataSource("match_partners API");
            setResults(alternativeData.results);
            generateGraphData(alternativeData.results);
          } else {
            setError("æœªæ‰¾åˆ°åŒ¹é…ç»“æœ");
          }
          return;
        }

        const data = await response.json();
        console.log("ä» matching-results è·å–çš„åŒ¹é…ç»“æœ:", data);

        if (data.results && data.results.length > 0) {
          setDataSource("matching-results API");
          setResults(data.results);
          generateGraphData(data.results);
        } else {
          setError("æœªæ‰¾åˆ°åŒ¹é…ç»“æœ");
        }
      } catch (apiError) {
        console.error("API è¯·æ±‚é”™è¯¯:", apiError);
        throw apiError;
      }
    } catch (error) {
      console.error("è·å–åŒ¹é…ç»“æœé”™è¯¯:", error);
      setError(error.message || "è·å–åŒ¹é…ç»“æœæ—¶å‡ºé”™");
    } finally {
      setIsLoading(false);
    }
  };

  const generateGraphData = (matchResults) => {
    if (!matchResults || matchResults.length === 0) {
      setGraphData({ nodes: [], links: [] });
      return;
    }

    console.log("ç”Ÿæˆå›¾è¡¨æ•°æ®ï¼ŒåŸºäºä»¥ä¸‹ç»“æœ:", matchResults);

    // å¤„ç†åŒ¹é…ç»“æœï¼Œæ·»åŠ åŒ¹é…ç±»åˆ«
    const processedResults = matchResults.map((result) => {
      // å¦‚æœç»“æœä¸­å·²ç»æœ‰ matchCategoryï¼Œç›´æ¥ä½¿ç”¨
      if (result.matchCategory) {
        return result;
      }

      // å¦åˆ™ï¼Œæ ¹æ®åŒ¹é…åˆ†æ•°ç”ŸæˆåŒ¹é…ç±»åˆ«
      const score = result.matchScore || 0;
      let matchCategory;

      if (score >= 90) {
        matchCategory = "ğŸŒŸ æä½³åŒ¹é…";
      } else if (score >= 75) {
        matchCategory = "âœ… è‰¯å¥½åŒ¹é…";
      } else if (score >= 50) {
        matchCategory = "ğŸŸ¡ ä¸€èˆ¬åŒ¹é…";
      } else if (score >= 25) {
        matchCategory = "ğŸ”´ ä½åŒ¹é…";
      } else {
        matchCategory = "âš«ï¸ ä¸åŒ¹é…";
      }

      return { ...result, matchCategory };
    });

    // æ›´æ–°å¤„ç†åçš„ç»“æœ
    setResults(processedResults);

    // ç”Ÿæˆç½‘ç»œå›¾æ•°æ®
    const nodes = processedResults.map((result) => ({
      id: result.id || result._id,
      name: result.name || result.orgName,
      matchCategory: result.matchCategory,
      score: result.matchScore,
      color: getColorByCategory(result.matchCategory),
    }));

    // æ·»åŠ å½“å‰ç”¨æˆ·èŠ‚ç‚¹
    nodes.push({
      id: "current-user",
      name: user?.orgName || "Your Organization",
      color: "#4CAF50",
    });

    // ç”Ÿæˆè¿æ¥ - æ‰€æœ‰ç»„ç»‡éƒ½ä¸å½“å‰ç”¨æˆ·ç›¸è¿
    const links = nodes
      .filter((node) => node.id !== "current-user")
      .map((node) => ({
        source: "current-user",
        target: node.id,
        value: (node.score || 50) / 100,
      }));

    setGraphData({ nodes, links });
  };

  const getColorByCategory = (category) => {
    const colorMap = {
      "ğŸŒŸ æä½³åŒ¹é…": "#4CAF50",
      "âœ… è‰¯å¥½åŒ¹é…": "#2196F3",
      "ğŸŸ¡ ä¸€èˆ¬åŒ¹é…": "#FFC107",
      "ğŸ”´ ä½åŒ¹é…": "#FF5722",
      "âš«ï¸ ä¸åŒ¹é…": "#9E9E9E",
    };
    return colorMap[category] || "#9E9E9E";
  };

  const handleSearch = (e) => {
    e.preventDefault();
    // å®ç°æœç´¢åŠŸèƒ½
    const filteredResults = results.filter(
      (result) =>
        (
          result.name?.toLowerCase() ||
          result.orgName?.toLowerCase() ||
          ""
        ).includes(searchQuery.toLowerCase()) ||
        (result.location?.toLowerCase() || "").includes(
          searchQuery.toLowerCase()
        )
    );
    setResults(filteredResults);
  };

  const handleReset = () => {
    setSearchQuery("");
    // é‡æ–°è·å–æ‰€æœ‰ç»“æœ
    fetchMatchingResults();
  };

  const debugInfo = () => {
    if (process.env.NODE_ENV !== "production") {
      return (
        <div className="mt-4 p-4 bg-gray-100 rounded-lg text-xs">
          <p className="font-bold">è°ƒè¯•ä¿¡æ¯ï¼ˆä»…å¼€å‘ç¯å¢ƒå¯è§ï¼‰:</p>
          <p>æ•°æ®æ¥æº: {dataSource || "æ— æ•°æ®"}</p>
          <p>ç»“æœæ•°é‡: {results.length}</p>
          <p>ç”¨æˆ·ID: {user?._id || user?.id || "æœªçŸ¥"}</p>
          <p>æœç´¢å‚æ•°: {JSON.stringify(searchParams)}</p>
          <button
            onClick={fetchMatchingResults}
            className="mt-2 px-2 py-1 bg-gray-300 rounded text-xs"
          >
            æ‰‹åŠ¨åˆ·æ–°æ•°æ®
          </button>
        </div>
      );
    }
    return null;
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* é¡¶éƒ¨å¯¼èˆªæ  */}
      <div
        className="App-header"
        style={{ height: "auto", minHeight: "auto", padding: "0" }}
      >
        <nav>
          <div className="logo">
            <img src={logo} alt="logo" />
            <span>Cause-connect</span>
          </div>
          <div className="nav-links">
            <a href="#">Explore</a>
            <Link to="/partnership-search">Partnership</Link>
            <a href="#">Solutions</a>
            <div className="profile-menu-container relative">
              <div
                className="flex items-center gap-1 ml-4 cursor-pointer"
                onClick={() => setIsProfileMenuOpen(!isProfileMenuOpen)}
              >
                <div className="w-8 h-8 rounded-full bg-gray-100 flex items-center justify-center">
                  <svg
                    className="w-5 h-5 text-gray-600"
                    fill="none"
                    stroke="currentColor"
                    viewBox="0 0 24 24"
                  >
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      strokeWidth="2"
                      d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z"
                    />
                  </svg>
                </div>
                <span className="text-sm text-gray-700 font-medium">
                  {user?.orgName}
                </span>
                <svg
                  className={`w-4 h-4 text-gray-500 transition-transform duration-200 ${
                    isProfileMenuOpen ? "transform rotate-180" : ""
                  }`}
                  fill="none"
